import React, { useState, useEffect, useRef } from "react";
import { Link } from "react-router-dom";

import { GlobalStyle, MainContainer } from "./sharedStyle";
import styled from "styled-components";
import { useScroll } from "./Works__/CustomHooks";

import { Footer } from "../components";

//Animation API
import AOS from "aos";
import "aos/dist/aos.css";

//#region 간판 스타일

const TextBox = styled.div`
  margin-bottom: 6rem;
  padding-right: 14px;
  word-break: keep-all;
  & > .box__title {
    font-size: 1.2em;
    font-weight: 600;
    margin-bottom: 0.8rem;
  }
`;

const Border = styled.div`
  position: relative;

  width: auto;
  height: 96vh;
  left: 2em;
  right: 2em;
  top: 2em;
  display: content-box;
  z-index: -2;

  @media screen and (max-width: 1080px) {
    left: 1em;
    right: 1em;
    top: 1em;
    bottom: 1em;
  }
`;

const Container = styled.div`
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -75%);
`;

const ScrollDownNotice = styled.div`
  position: sticky;
  width: 50px;
  height: 100px;
  top: 70vh;
  transform: translate(50%, 0);
  z-index: 3;

  transition: 0.2s;
  animation-duration: 1s;
  animation-name: "popLoop";
  animation-iteration-count: infinite;
  animation-direction: alternate;

  & > .arrow {
    fill: #3d3d3d;
    stroke: #3d3d3d;
    stroke-width: 8;
    stroke-linecap: round;
    stroke-linejoin: round;
  }

  &.scrolled {
    opacity: 0;
  }

  @keyframes popLoop {
    from {
      bottom: 5%;
    }
    to {
      bottom: 8%;
    }
  }
`;

// const LinkContainer = styled.div`
//   display: flex;
//   flex-wrap: wrap;
//   justify-content: center;
// `;

const Contents = styled.div`
  position: relative;
`;

const TitleImg = styled.svg`
  width: 25rem;
  fill: #fff;

  .st0 {
    animation-name: lineAnim;
    animation-duration: 5s;
    animation-timing-function: ease-in-out;
    animation-fill-mode: forwards;

    stroke-width: 2px;
    stroke: black;
    stroke-dasharray: 0 300;
  }

  .circle,
  .poly,
  .rect {
    animation-name: fills;
    animation-duration: 5s;
    animation-timing-function: ease-in-out;
    animation-fill-mode: forwards;
  }

  @keyframes lineAnim {
    to {
      stroke-width: 0px;
      stroke-dasharray: 160 0;
    }
  }

  @keyframes fills {
    to {
      fill: #303030;
      opacity: 1;
    }
  }

  @media screen and (max-width: 1080px) {
    width: 70vw;
  }
`;

//#endregion

//#region About 스타일
const AbMainContainer = styled(MainContainer)`
  /* position: absolute; */
  /* top: 100%; */
  background-color: #f9f9f9;
  margin-top: 5vh;
  height: auto;
  overflow-y: hidden;
  font-size: 1.2rem;
  box-sizing: content-box;
  @media screen and (max-width: 1080px) {
    padding: 0 2%;
    font-size: 1rem;
  }

  & > .main__introImage {
    margin-top: 18vh;
    margin-bottom: 12vh;
    height: auto;
    display: grid;
    grid-template-columns: repeat(8, 1fr);
    grid-template-rows: 50vh;
    grid-auto-rows: max-content;

    @media screen and (max-width: 800px) {
      margin-top: 4vh;
      grid-template-columns: 1fr;
    }

    & > .introContainer {
      &.first {
        grid-column: 2 / span 3;
      }
      &.second {
        grid-column: span 3;
      }
      height: fit-content;
      margin: auto;
      width: 80%;

      & > .circle,
      .polygon {
        margin: auto;
        width: 65%;

        @media screen and (max-width: 1080px) {
          width: 55%;
        }
      }

      & > .circle {
        fill: #ff9f31;
      }

      & > .polygon {
        fill: #6a99ff;
      }

      & > .text {
        text-align: center;
        font-size: 2.3rem;
        font-weight: 500;

        @media screen and (max-width: 1080px) {
          font-size: 1.5rem;
          font-weight: normal;
        }
      }
    }
  }

  & > .text_container {
    max-width: 45rem;
    display: grid;
    grid-template-columns: 1fr;
    width: auto;
    margin: auto;
    margin-top: 5rem;

    & > .horizontal-divider {
      margin: auto;
      margin-top: 100px;
      margin-bottom: 100px;
      width: 100%;
      height: 1px;
      background-color: gray;
    }

    & > .btn {
      justify-self: center;
    }
  }

  & > .about_intro {
    margin: auto;
  }
`;

const TestButton = styled.div`
  width: 100px;
  height: 100px;
  background-color: skyblue;
  position: fixed;
  top: 400px;
  left: 100px;
`;

const WorksButton = styled.button`
  display: inline-block;
  font-size: 1.2rem;
  font-weight: 600;
  width: auto;
  margin: auto;
  padding: 1rem 2rem;
  color: white;
  background-color: #4cc790;
  border: 2px black solid;
  cursor: pointer;
  transition: 0.2s;

  &:hover {
    background-color: #79dfb1;
  }
  &:active {
    background-color: #3f9b72;
  }
`;
//#endregion

/**
 * Function
 */
//#region function
export default function Welcome() {
  const [isScroll, setIsScroll] = useState(false);
  const violationRef = useRef();
  const { scrollY } = useScroll();

  useEffect(() => {
    AOS.init({ once: true });
    console.log("Welcome page rendered.");
    window.scrollTo(0, 0);
  }, []);

  useEffect(() => {
    if (scrollY > 1000) {
      setIsScroll(true);
    } else {
      setIsScroll(false);
    }
  }, [scrollY]);

  return (
    <>
      <ScrollDownNotice className={isScroll ? "scrolled" : "scroll_down"}>
        <svg
          className="arrow"
          version="1.1"
          id="Layer_1"
          xmlns="http://www.w3.org/2000/svg"
          xlink="http://www.w3.org/1999/xlink"
          x="0px"
          y="0px"
          viewBox="0 0 50 56.22"
          space="preserve"
        >
          <path d="M27.8,42.1l8.6-15c1.3-2.3-0.3-5.1-3-5.1H16.2c-2.6,0-4.3,2.9-3,5.1l8.6,15C23.2,44.4,26.5,44.4,27.8,42.1z" />
        </svg>
      </ScrollDownNotice>

      <Border className="Border_container">
        <Container className="welcome_container">
          <Contents>
            {/* <Title>Pyoeng</Title> */}
            <TitleImg
              onAnimationEnd={() => {
                isScroll
                  ? console.log("scrolled")
                  : violationRef.current.scrollIntoView({
                      behavior: "smooth",
                      block: "start",
                    });
              }}
              version="1.1"
              id="Layer_1"
              xmlns="http://www.w3.org/2000/svg"
              x="0px"
              y="0px"
              viewBox="0 0 125 100"
            >
              <rect x="5.5" y="33.17" className="st0" width="15" height="60" />
              <rect
                x="105.5"
                y="33.17"
                className="st0"
                width="15"
                height="60"
              />
              <circle className="st0" cx="42.06" cy="52.23" r="19.06" />
              <polygon
                className="st0"
                points="78.4,34.07 65.45,34.07 65.44,34.08 65.44,47.05 89.68,71.29 102.64,71.29 102.65,71.28 102.65,58.32"
              />

              <rect
                x="5.5"
                y="33.17"
                className=" rect"
                width="15"
                height="60"
              />
              <rect
                x="105.5"
                y="33.17"
                className=" rect"
                width="15"
                height="60"
              />
              <circle className=" circle" cx="42.06" cy="52.23" r="19.06" />
              <polygon
                className=" poly"
                points="78.4,34.07 65.45,34.07 65.44,34.08 65.44,47.05 89.68,71.29 102.64,71.29 102.65,71.28 102.65,58.32"
              />
            </TitleImg>
          </Contents>
        </Container>
      </Border>

      <AbMainContainer className="ABMContainer" ref={violationRef}>
        <div className="main__introImage">
          <div
            className="introContainer first"
            data-aos="fade-left"
            data-aos-offset="300"
          >
            <div className="circle">
              <svg
                viewBox="0 0 186 180"
                version="1.1"
                xmlns="http://www.w3.org/2000/svg"
              >
                <circle className="c" cx="89.34" cy="89.85" r="85.71" />
              </svg>
            </div>
            <div className="text 01" data-aos="fade-up" data-aos-delay="100">
              저는 디자이너입니다.
            </div>
          </div>

          <div
            className="introContainer second"
            data-aos="fade-right"
            data-aos-delay="700"
            data-aos-offset="300"
          >
            <div className="polygon">
              <svg
                viewBox="186 0 186 180"
                version="1.1"
                xmlns="http://www.w3.org/2000/svg"
              >
                <polygon
                  className="p"
                  points="276.7,4.14 197.06,4.14 197.01,4.19 197.01,83.92 288.65,175.56 368.33,175.56 368.38,175.51 
                              368.38,95.82 "
                />
              </svg>
            </div>

            <div className="text 02" data-aos="fade-up" data-aos-delay="800">
              그리고 엔지니어입니다.
            </div>
          </div>
          <div className="text-holder" data-aos="fade-up"></div>
        </div>

        <div className="a text_container">
          <TextBox data-aos="fade-up" data-aos-offset="300">
            <div className="box__title">어떻게 그래?</div>
            학부 과정으로 시각디자인을, 석사 과정으로 감성공학을 전공했습니다.
            <br />
            <br />
            디자인을 배우던 중 인터랙티브 디자인, 미디어아트 등에 관심을 가져
            구현 도구로서 유니티를 시작했고, 발전시키기 위해 공학 대학원을
            진학하게 되었습니다.
            <br />
          </TextBox>

          <TextBox data-aos="fade-up" data-aos-offset="300">
            <div className="box__title">데이터 기반 디자인을 지향</div>
            UI/UX 관련 프로젝트들을 진행했었고 디자이너 직관을 넘은 데이터에
            기반한 인터페이스와 사용자 플로우를 지향합니다.
            <br />
            사용자의 행태 분석 및 플로우 개선을 위한 데이터 수집을 염두하며
            작업하려 노력합니다.
            <br />
            GUI 제작을 위한 그래픽 툴을, UX 구현을 위한 프로토타이핑 툴을 사용할
            수 있습니다.
          </TextBox>

          <TextBox data-aos="fade-up" data-aos-offset="300">
            <div className="box__title">프로그래밍의 길로 나아가는중</div>
            인터랙티브 플랫폼에 UI를 구현하는 프론트엔드를 목표로 하고 있습니다.
            <br />
            프론트엔드를 목표로 Unity, javascript, Swift를 학습했었고 사용할 수
            있습니다.
            <br />
            시스템 생태계를 이해하여 가장 효율적인 디자인을 수행하는 것이
            궁극적인 목표입니다.
          </TextBox>

          <TextBox data-aos="fade-up" data-aos-offset="300">
            <div className="box__title">예술과 기술의 줄타기</div>
            미디어아트, 키네틱 아트 등 기술과 과학을 이용한 예술을 굉장히
            좋아합니다.
            <br />
            유니티의 Visual graph, Shader Graph 등을 이용한 표현에 큰 관심을
            갖고있습니다.
            <br />
            기술과 예술 양쪽으로 생각회로가 뻗쳐있기 때문에 일상의 자극에서도
            복합적인 영감을 얻곤 합니다.
          </TextBox>

          <TextBox data-aos="fade-up" data-aos-offset="300">
            <div className="box__title">새로운 배움에 적극적</div>
            배움의 동력은 발전하려는 욕심입니다.
            <br />
            발전하려는 욕구를 가지고 있기 때문에 학습에 높은 흥미를 보입니다.
            <br />
            새로운 기술에 흥미를 갖고 깨어있으려 노력하며 배움에 거부감이 적다고
            자부합니다.
            <br />
            <br />
            원하는것을 구현하기 위해 수단과 방법을 가리지 않고 이것저것
            배웠습니다.
            <br />
