import React from "react";
import { Link } from "react-router-dom";
import styled from "styled-components";

const ThumbImg = styled.img`
  min-width: 20%;
  max-width: 30%;
`;

const TextWrapperContainer = styled.div`
  display: flex;
`;

const ItemCells = styled(Link)`
  /* position: relative; */
  border-top: 2px solid;
  border-bottom: 2px solid;
  margin-bottom: -2px;

  display: flex;
  padding-top: 4vh;
  padding-bottom: 4vh;
  padding-left: 1.5%;
  padding-right: 1.5%;
  box-sizing: border-box;
  filter: grayscale(100%);
  cursor: pointer;

  transition: 0.5s;

  &.underConstruction {
    pointer-events: none;
  }

  &:hover {
    filter: grayscale(0%);
  }

  & > .panel {
    width: 100%;
    height: 100%;
    position: absolute;
    background-color: black;
  }

  ${TextWrapperContainer} {
    flex: 1;
    padding-left: 4%;
    flex-flow: column;

    & > .titleContainer {
      margin-top: -0.8rem;
      margin-bottom: 1rem;
      font-size: 3rem;
      font-weight: 500;
    }

    & > .descContainer {
      margin-bottom: 0.5rem;
      font-size: 1.2rem;
      font-weight: 300;
    }

    @media screen and (max-width: 1080px) {
      & > .titleContainer {
        font-size: 2rem;
        font-weight: 500;
      }
    }
  }

  @media screen and (max-width: 1080px) {
    filter: none;

    width: 100%;
    padding-left: 3%;
    padding-right: 3%;
  }
`;

export default function ItemCell(props) {
  return (
    <ItemCells
      className={props.isConstruction ? "underConstruction" : ""}
      to={`${props.path}/${props.alt}`}
    >
      <ThumbImg alt={props.alt} src={props.thumbImg} />
      <TextWrapperContainer>
        <div className="titleContainer">
          <span>{props.title}</span>
        </div>
        <div className="descContainer">
          <span>{props.desc}</span>
        </div>
        <div className="category">
          <span>{props.subtitle}</span>
        </div>
      </TextWrapperContainer>
    </ItemCells>
  );
}
