import React from "react";
import { Link } from "react-router-dom";
import styled from "styled-components";

const ThumbImg = styled.figure`
  max-width: 25%;

  img {
    width: 100%;
    cursor: pointer;
  }

  .categoryHolder {
    position: absolute;
    left: 25%;
    width: auto;
    display: flex;
    border: red 2px solid;

    & > .c1 {
      width: 100px;
    }
  }
`;

const TextWrapperContainer = styled.div``;

const ItemCells = styled.div`
  /* position: relative; */
  border-top: 2px solid;
  border-bottom: 2px solid;
  margin-bottom: -2px;

  display: flex;
  padding-top: 4vh;
  padding-bottom: 4vh;
  box-sizing: border-box;
  filter: grayscale(100%);

  transition: 0.5s;

  &.underConstruction {
    pointer-events: none;
  }

  &:hover {
    filter: grayscale(0%);
  }

  & > .panel {
    width: 100%;
    height: 100%;
    position: absolute;
    background-color: black;
  }

  ${TextWrapperContainer} {
    display: flex;
    flex: 1;
    padding-left: 4%;
    flex-flow: column;

    & > .titleContainer {
      max-width: 67%;
      display: flex;
      justify-content: space-between;
      line-height: 4.5rem;
      margin-bottom: 1rem;
      font-size: 3.5rem;
      font-weight: 500;

      & > a {
        &::after {
          position: absolute;
          right: 3rem;
          opacity: 0;
          transition: 0.3s;
          flex: 0.2;
          content: "--›";
        }

        &:hover:after {
          opacity: 1;
        }
      }
    }

    & > .descContainer {
      max-width: 60%;
      margin-bottom: 1rem;
      font-size: 1.1rem;
      font-weight: 300;
    }

    @media screen and (max-width: 1080px) {
      & > .titleContainer {
        line-height: initial;
        font-size: 2rem;
        font-weight: 500;
      }
    }
  }

  @media screen and (max-width: 1080px) {
    filter: none;

    width: 100%;
    padding-left: 3%;
    padding-right: 3%;
  }
`;

export default function ItemCell(props) {
  return (
    <ItemCells className={props.isConstruction ? "underConstruction" : ""}>
      <ThumbImg>
        <Link to={`${props.path}/${props.alt}`}>
          <div className="categoryHolder">
            <div className="c1">
              <svg
                viewBox="0 0 186 180"
                version="1.1"
                xmlns="http://www.w3.org/2000/svg"
              >
                <circle className="c" cx="89.34" cy="89.85" r="85.71" />
              </svg>
            </div>
            <div className="c1">
              <svg
                viewBox="0 50 186 180"
                version="1.1"
                xmlns="http://www.w3.org/2000/svg"
              >
                <polygon
                  className="p"
                  points="276.7,4.14 197.06,4.14 197.01,4.19 197.01,83.92 288.65,175.56 368.33,175.56 368.38,175.51 
                              368.38,95.82 "
                />
              </svg>
            </div>
          </div>
          <img alt={props.alt} src={props.thumbImg} />
        </Link>
      </ThumbImg>
      <TextWrapperContainer>
        <div className="titleContainer">
          <Link to={`${props.path}/${props.alt}`}>
            <span>{props.title}</span>
          </Link>
        </div>
        <div className="descContainer">
          <span>{props.desc}</span>
        </div>
        <div className="category">
          <span>
            <strong>Category: </strong>
            {props.subtitle}
          </span>
        </div>
      </TextWrapperContainer>
    </ItemCells>
  );
}
