import React, { useState, useEffect, Suspense } from "react";
import styled, { css } from "styled-components";
import { ss } from "..";
import JSON_DATA from "../../Data3.json";

import Header from "../../components/Header";
import IntroHolder from "../../components/IntroHolder";
import ContributionChart from "../../components/ContributionChart";
import LinkButton from "../../components/Button";

import { fetchJsonData } from "../../shared/FetchApi";

// import {* as viaf} from "../../image";

export default function Viaf2021({ match, history }) {
  // JSON 파일을 만들어서 ContentsId랑 일치하는 오브젝트 불러오기

  // var data = null;
  var imagesUrls = [];
  var imagesObjectUrls = [];
  var videoUrls = [];
  const contentsId = match.params.contentsId;

  // const [currentData, setCurrentData] = useState(() => {
  //   JSON_DATA.map((item) => {
  //     if (item.id == contentsId) {
  //       data = item;

  //       data.images.map((item) => {
  //         //배열 형태에 전달
  //         imagesUrls.push(process.env.PUBLIC_URL + item);

  //         //오브젝트 형태에 전달 <- 이미지갤러리 등에 쓰는 경우
  //         imagesObjectUrls.push({ original: process.env.PUBLIC_URL + item });
  //         return;
  //       });

  //       // 비디오+public 폴더 주소 합치기
  //       data.videos.map((item) => {
  //         videoUrls.push(process.env.PUBLIC_URL + item);
  //         return;
  //       });
  //     } else {
  //       return <div>Somethings wrong!</div>;
  //     }
  //   });
  // });
  const [data, setData] = useState();
  const [isScrolled, setScrollActive] = useState(false);
  const [scrollValue, setScrollValue] = useState(0);

  const handleScroll = () => {
    const scrollPos = window.scrollY;
    setScrollValue(scrollPos);
  };

  useEffect(() => {
    fetchData();

    window.scrollTo(0, 0);
    window.removeEventListener("scroll", handleScroll);
    window.addEventListener("scroll", handleScroll);

    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  // fetch JSON data
  function fetchData() {
    fetch(process.env.PUBLIC_URL + "/Data3.json", {
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
      },
    })
      .then((response) => {
        return response.json();
      })
      .then((myData) => {
        setData(myData);
      });
  }

  useEffect(() => {}, []);

  function IntroPage() {
    console.log(data);
    const d = data.map((item) => {
      if (item.id == contentsId) {
        console.log(item);

        // data = item;

        // data.images.map((item) => {
        //   //배열 형태에 전달
        //   imagesUrls.push(process.env.PUBLIC_URL + item);

        //   //오브젝트 형태에 전달 <- 이미지갤러리 등에 쓰는 경우
        //   imagesObjectUrls.push({ original: process.env.PUBLIC_URL + item });
        //   return;
        // });

        // // 비디오+public 폴더 주소 합치기
        // data.videos.map((item) => {
        //   videoUrls.push(process.env.PUBLIC_URL + item);
        //   return;
        // });
      } else {
        return <div>Somethings wrong!</div>;
      }
    });
    console.log("title::" + d);
    return (
      <IntroHolder
        title={d.title}
        client={d.client}
        period={d.period}
        subtitle={d.subtitle}
        team={d.team}
        // results={data.results.map((item, idx) => {
        //   return (
        //     <span key={idx}>
        //       {item}
        //       <br />
        //     </span>
        //   );
        // })}
      />
    );
  }

  // JSON_DATA.map((item) => {
  //   if (item.id == contentsId) {
  //     data = item;
  //     return;
  //   }
  //   return <div>Somethings wrong!</div>;
  // });

  // // 이미지+public 폴더 주소 합치기
  // data.images.map((item) => {
  //   //배열 형태에 전달
  //   imagesUrls.push(process.env.PUBLIC_URL + item);

  //   //오브젝트 형태에 전달 <- 이미지갤러리 등에 쓰는 경우
  //   imagesObjectUrls.push({ original: process.env.PUBLIC_URL + item });
  //   return;
  // });

  // // 비디오+public 폴더 주소 합치기
  // data.videos.map((item) => {
  //   videoUrls.push(process.env.PUBLIC_URL + item);
  //   return;
  // });

  return (
    <>
      <Header nav={"WORKS"} />
      <ss.HeadContainer textColor="white">
        {/* <ss.BackBtn onClick={() => history.goBack()}>뒤로</ss.BackBtn> */}
        <ss.HeadContainerImg src={require("../../image/vban.jpg").default} />
        <div className="titleText Holder">
          <div className="01">VIA Festival 2021</div>
        </div>
      </ss.HeadContainer>
      <ss.Background>
        <ss.MainContainer className={isScrolled ? "scrolled" : ""}>
          <div className="subContainer">
            <ss.TextHolder first={true}>
              <div className="title">
                <div className="bar_start" />
                <div className="text">가상의 미로를 탐방하는 웹 인터랙티브</div>
              </div>

              <div className="desc">
                <div>
                  '길(VIA)'위에서 펼쳐져 온 비아페스티벌(VIA Festival)이
                  코로나로 인해 온라인 축제로 진행되었습니다. 일반적인 온라인
                  축제의 나열식 배치에서 벗어나 미로형태의 '길'을 구현함과
                  동시에 '게임'형식을 접목하여 예술 작품을 감상하기 위한 과정을
                  재현하였습니다. 사용자는 터치 조이스틱을 이용해 미로에 있는
                  꽃(예술)을 만나게 됩니다.
                </div>
              </div>
              <img
                src={imagesUrls[7]}
                className="holder__image"
                alt="concept"
                style={{
                  gridColumn: "3 span",
                  width: "100%",
                  height: "auto",
                }}
              />
              <div className="buttonHolder">
                <LinkButton
                  btnText="체험해보기"
                  link="https://viaf2021.github.io/viaf2021/"
                />
              </div>
            </ss.TextHolder>
          </div>

          <ss.TechContainer>
            <div className="techHolder__title">담당업무 / 기여도</div>
            <ss.TechHolder>
              {data.jobs.map((item, idx) => {
                return (
                  <ContributionChart
                    key={"chart" + idx}
                    className="techHolder__chart"
                    jobName={item.job_name}
                    myJob={item.my_job}
                    withTeam={item.with_team}
                    percent={item.percentage}
                  />
                );
              })}
            </ss.TechHolder>
          </ss.TechContainer>

          <ss.SideImage className="sideImage" topTo={scrollValue / 3 + "px"}>
            <img
              src={process.env.PUBLIC_URL + "/img/svg/aboutCircle.svg"}
            ></img>
          </ss.SideImage>
          {/* <ss.SvgImg
            type="image/svg+xml"
            data={process.env.PUBLIC_URL + "/img/svg/aboutCircle.svg"}
            top={"calc(30% - 200px)"}
            left="-20%"
          /> */}

          <div className="subContainer">
            <ss.TextHolder>
              <div className="title">
                <div className="bar_start" />
                <div className="text">유니티 WebGL 프로젝트</div>
                <div className="bar_end" />
              </div>
              <div className="desc">
                <div>
                  프로젝트를 유니티의 WebGL로 빌드하여 웹에 게시하였습니다.
                  <br />
                  단조로운 길의 느낌을 미로에 표현해야 했기 때문에, 약간의
                  재질만을 추가해 미로를 생성했습니다.
                  <br />
                  웹과 유니티를 연동하여 신호를 주고받기 위해 jslib 파일을
                  생성하여 사용했습니다.
                  <br />
                  미로는 2D 벡터에서 3차원 fbx로 만든 뒤 유니티로 불러와
                  사용했습니다.
                </div>
              </div>
            </ss.TextHolder>
          </div>
          <ss.ImageHolder>
            <ss.Holder__image
              src={imagesUrls[6]}
              className="holder__image"
              alt="img01"
              column={"3 / 8 span"}
            />

            <ss.Holder__div column={"4 / 6 span"}>
              <img
                style={{
                  width: "100%",
                  height: "100%",
                  zIndex: "2",
                  position: "relative",
                }}
                src={imagesUrls[9]}
              ></img>
              <video
                style={{
                  position: "absolute",
                  left: "9%",
                  top: "3%",
                  height: "92%",
                }}
                autoPlay
                muted
                loop
                controls={false}
                src={videoUrls[0]}
              ></video>
            </ss.Holder__div>

            {/* <ss.Holder__image
              src={imagesUrls[8]}
              className="holder__image"
              alt="phone"
              column={"4 / 6 span"}
              marginBottom="2 rem"
            /> */}
          </ss.ImageHolder>

          <div className="subContainer">
            <ss.TextHolder>
              <div className="title">
                <div className="bar_start" />
                <div className="text">꽃 컨셉에 충실하게</div>
                <div className="bar_end" />
              </div>
              <div className="desc">
                <div>
                  중심 컨셉이 꽃이였기 때문에 꽃의 이미지를 주요하게
                  사용하였습니다.
                  <br />
                  꽃 모양으로 공연예술의 세가지 분류를 표현했습니다. 영상을 본
                  뒤 팝업을 닫으면 꽃잎을 흩뿌려 정적인 공간에 부드러움을
                  더하고자 했습니다.
                  <br />
                  팝업창에 꽃 이미지를 배치하여 공연예술 분류와 동시에 풍성한
                  이미지를 연출했습니다.
                </div>
              </div>
            </ss.TextHolder>
          </div>

          <ss.ImageHolder>
            <ss.Holder__image
              src={imagesUrls[2]}
              className="holder__image"
              column={"3 / 8 span"}
              alt="img01"
              objPos="0% 0"
            />
            <ss.Holder__image
              src={imagesUrls[5]}
              className="holder__image"
              column={"3 / 8 span"}
              alt="img02"
            />
            <ss.Holder__image
              src={imagesUrls[4]}
              className="holder__image"
              column={"3 / 4 span"}
              alt="img03"
            />
            <ss.Holder__image
              src={imagesUrls[3]}
              className="holder__image"
              column={"7 / 4 span"}
              alt="img04"
            />
          </ss.ImageHolder>

          <div className="subContainer">
            <ss.TextHolder>
              <div className="title">
                <div className="bar_start" />
                <div className="text">프로젝트 종료 후</div>
                <div className="bar_end" />
              </div>
              <div className="desc">
                <div>
                  유니티의 멀티 플랫폼 디버깅 기능을 사용해 본 것이 처음이였기
                  때문에 굉장히 많이 헤맸습니다.
                  <br />
                  WebGL을 기반으로 하기 때문에 안드로이드를 기준으로 작업한
                  내용이 iOS에서는 전혀 동작하지 않는다던지 하는 호환성 문제
                  때문에 낮은 버전의 WebGL을 사용할 수 밖에 없었습니다.
                  <br />
                  버전을 낮추게 되니 유니티의 Global Illumination 렌더러에도
                  한계가 생겨 더 화사하게 꾸미지 못한게 아쉬운 작업이였습니다.
                </div>
              </div>
            </ss.TextHolder>
          </div>
        </ss.MainContainer>
      </ss.Background>
    </>
  );
}
