import React from "react";
import Header from "../components/Header";
import styled from "styled-components";

//Import Components
import CategoryTitleDiv from "../components/CategoryTitleDiv";
import ItemCell from "../components/ItemCell";

//Import Style
import { MainContainer } from "./sharedStyle";

//Import JSON data
import JSON_DATA from "../Data.json";
// import JSON_DATA from "../Data2.json";

const CategoryContainer = styled.div`
  float: left;
  width: 100%;
  margin-bottom: 5vh;
`;

function getJsonData(JSON) {
  const catList = ["UI/UX", "Graphics", "Web"];
  const uiData = JSON.filter((item) => item.category == "UI/UX");
  return uiData;
}

export default function WorksList({ match }) {
  const path = match.path;

  //
  const dt = getJsonData(JSON_DATA);
  console.log(dt);
  //

  window.scrollTo(0, 0);
  var categoryName = null;
  var innerDataArray = null;

  return (
    <>
      <Header nav="Works" />

      <MainContainer>
        <div className="list">
          {JSON_DATA &&
            JSON_DATA.map((item, idx) => {
              // JSON 오브젝트 나누기
              var a = 0;
              const [keys, entryValues] = Object.entries(item);
              const categoryName = keys[0];
              const innerDataArray = keys[1];
              a += 1;
              return (
                // 카테고리 / 내부데이터 나눠서 생성
                <CategoryContainer key={"cc" + idx}>
                  <CategoryTitleDiv key={"ct" + idx} desc={categoryName} />
                  {innerDataArray.map((element) => {
                    return (
                      <ItemCell
                        key={element.id}
                        path={path}
                        alt={element.id}
                        thumbImg={process.env.PUBLIC_URL + element.thumb_img}
                        title={element.title}
                        category={element.description}
                      />
                    );
                  })}
                </CategoryContainer>
              );
            })}
        </div>
      </MainContainer>
    </>
  );
}
