import styled from "styled-components";

export const MainContainer = styled.div `
  height: auto;
  padding-top: 10em;
  padding-left: 17%;
  padding-right: 17%;
  background-color: white;

  @media screen and (max-width: 1080px) {
    padding-left: 10%;
    padding-right: 10%;
  }
`;

export const CategoryContainer = styled.div `
  float: left;
  width: 100%;
  margin-bottom: 5vh;
`;
