import React, { useState, useEffect } from "react";
import { ss } from "..";
// import JSON_DATA from "../../Data2.json";
import JSON_DATA from "../../Data3.json";

import Header from "../../components/Header";
import IntroHolder from "../../components/IntroHolder";
import ContributionChart from "../../components/ContributionChart";
import LinkButton from "../../components/Button";

export default function Cardivu({ match, history }) {
  // JSON 파일을 만들어서 ContentsId랑 일치하는 오브젝트 불러오기
  const contentsId = match.params.contentsId;
  const [isScrolled, setScrollActive] = useState(false);
  var data = null;
  var imagesUrls = [];
  var imagesObjectUrls = [];
  var videoUrls = [];

  window.scrollTo(0, 0);

  JSON_DATA.map((item) => {
    if (item.id == contentsId) {
      data = item;
      return;
    }
    return <div>Somethings wrong!</div>;
  });

  // 이미지+public 폴더 주소 합치기
  data.images.map((item) => {
    //배열 형태에 전달
    imagesUrls.push(process.env.PUBLIC_URL + item);

    //오브젝트 형태에 전달 <- 이미지갤러리 등에 쓰는 경우
    imagesObjectUrls.push({ original: process.env.PUBLIC_URL + item });
    return;
  });

  // 비디오+public 폴더 주소 합치기
  data.videos.map((item) => {
    videoUrls.push(process.env.PUBLIC_URL + item);
    return;
  });

  return (
    <>
      <Header nav={"WORKS"} />
      <ss.HeadContainer textColor="white">
        <ss.HeadContainerImg imageUrl={data.link_banner} />
        {/* <ss.BackBtn onClick={() => history.goBack()}>뒤로</ss.BackBtn> */}
        <div className="titleText Holder">
          <div className="01">Cardivu</div>
        </div>
      </ss.HeadContainer>

      <ss.Background>
        <IntroHolder
          title={data.title}
          client={data.client}
          period={data.period}
          subtitle={data.subtitle}
          team={data.team}
          results={data.results.map((item, idx) => {
            return (
              <span key={idx}>
                {item}
                <br />
              </span>
            );
          })}
        />

        <ss.MainContainer className={isScrolled ? "scrolled" : ""}>
          {/* SVG 이미지 로드 */}
          <ss.SvgImg
            type="image/svg+xml"
            data={process.env.PUBLIC_URL + "/img/svg/aboutCircle.svg"}
            top="23%"
            left="-20%"
          />

          <div className="subContainer">
            <ss.TextHolder first={true}>
              <div className="title">
                <div className="bar_start" />
                <div className="text">홍채인식을 통한 심박 추론 앱</div>
              </div>

              <div className="desc">
                <div>이전 직장에서 만듦</div>
              </div>

              {/* <LinkButton link="https://viaf2021.github.io/viaf2021/" /> */}
            </ss.TextHolder>
          </div>

          <ss.TechContainer>
            <div className="techHolder__title">담당업무 / 기여도</div>
            <ss.TechHolder>
              {data.jobs.map((item, idx) => {
                return (
                  <ContributionChart
                    key={"chart" + idx}
                    className="techHolder__chart"
                    jobName={item.job_name}
                    myJob={item.my_job}
                    withTeam={item.with_team}
                    percent={item.percentage}
                  />
                );
              })}
            </ss.TechHolder>
          </ss.TechContainer>

          <div className="subContainer">
            <ss.TextHolder>
              <div className="title">
                <div className="bar_start" />
                <div className="text">건강관리 앱을 목표로</div>
                <div className="bar_end" />
              </div>
              <div className="desc">
                <div>
                  건강관리 앱을 목표로 했기 때문에 부드럽고 신뢰를 줄 수
                  있을만한 색을 컨셉으로 제작
                </div>
              </div>
            </ss.TextHolder>
          </div>

          <div className="subContainer">
            <ss.TextHolder>
              <div className="title">
                <div className="bar_start" />
                <div className="text">유선형 디자인</div>
                <div className="bar_end" />
              </div>
              <div className="desc">
                <div>
                  소화기 이미지에서 오는 유기적인 느낌을 표현하고자 유선형
                  디자인을 채택했습니다.
                  <br />
                  인터페이스는 둥근 형태를 기본형으로, 배경 장식들은 자유롭게
                  흐르는 모양으로 제작했습니다.
                  <br />
                  기획 단계에서 부드러운 느낌을 주고 싶었기 때문에 파스텔톤에
                  가까운 컬러를 사용했습니다.
                </div>
              </div>
            </ss.TextHolder>
          </div>

          <ss.ImageHolder>
            <ss.Holder__image
              src={imagesUrls[2]}
              className="holder__image"
              column={"3 / 8 span"}
              alt="img01"
              objPos="0% 0"
            />
            <ss.Holder__image
              src={imagesUrls[5]}
              className="holder__image"
              column={"3 / 8 span"}
              alt="img02"
            />
            <ss.Holder__image
              src={imagesUrls[4]}
              className="holder__image"
              column={"3 / 4 span"}
              alt="img03"
            />
            <ss.Holder__image
              src={imagesUrls[3]}
              className="holder__image"
              column={"7 / 4 span"}
              alt="img04"
            />
          </ss.ImageHolder>

          <div className="subContainer">
            <ss.TextHolder>
              <div className="title">
                <div className="bar_start" />
                <div className="text">프로젝트 종료 후</div>
                <div className="bar_end" />
              </div>
              <div className="desc">
                <div>완성이 안된게 아쉬운점이다~</div>
              </div>
            </ss.TextHolder>
          </div>
        </ss.MainContainer>
      </ss.Background>
    </>
  );
}
