import React, { useState, useEffect } from "react";
import { ss } from "..";
// import JSON_DATA from "../../Data2.json";
import JSON_DATA from "../../Data3.json";

import Header from "../../components/Header";
import IntroHolder from "../../components/IntroHolder";
import ContributionChart from "../../components/ContributionChart";
import LinkButton from "../../components/Button";

export default function Heartfit({ match, history }) {
  // JSON 파일을 만들어서 ContentsId랑 일치하는 오브젝트 불러오기
  const contentsId = match.params.contentsId;
  const [isScrolled, setScrollActive] = useState(false);
  var data = null;
  var imagesUrls = [];
  var imagesObjectUrls = [];
  var videoUrls = [];

  window.scrollTo(0, 0);

  JSON_DATA.map((item) => {
    if (item.id == contentsId) {
      data = item;
      return;
    }
    return <div>Somethings wrong!</div>;
  });

  // 이미지+public 폴더 주소 합치기
  data.images.map((item) => {
    //배열 형태에 전달
    imagesUrls.push(process.env.PUBLIC_URL + item);

    //오브젝트 형태에 전달 <- 이미지갤러리 등에 쓰는 경우
    imagesObjectUrls.push({ original: process.env.PUBLIC_URL + item });
    return;
  });

  // 비디오+public 폴더 주소 합치기
  data.videos.map((item) => {
    videoUrls.push(process.env.PUBLIC_URL + item);
    return;
  });

  return (
    <>
      <Header nav={"WORKS"} />
      <ss.HeadContainer textColor="white">
        <ss.HeadContainerImg imageUrl={data.link_banner} />
        {/* <ss.BackBtn onClick={() => history.goBack()}>뒤로</ss.BackBtn> */}
        <div className="titleText Holder">
          <div className="01">HeartFit</div>
        </div>
      </ss.HeadContainer>

      <IntroHolder
        title={data.title}
        client={data.client}
        period={data.period}
        subtitle={data.subtitle}
        team={data.team}
        results={data.results.map((item, idx) => {
          return (
            <span key={idx}>
              {item}
              <br />
            </span>
          );
        })}
      />

      <ss.MainContainer className={isScrolled ? "scrolled" : ""}>
        {/* SVG 이미지 로드 */}
        <ss.SvgImg
          type="image/svg+xml"
          data={process.env.PUBLIC_URL + "/img/svg/aboutCircle.svg"}
          top="200%"
          left="-20%"
        />

        <div className="subContainer">
          <ss.TextHolder first={true}>
            <div className="title">
              <div className="bar_start" />
              <div className="text">얼굴인식으로 생체정보 추론하는 앱</div>
            </div>

            <div className="desc">
              <div>123</div>
            </div>

            <LinkButton link="https://viaf2021.github.io/viaf2021/" />
          </ss.TextHolder>
        </div>

        <ss.TechContainer>
          <div className="techHolder__title">담당업무 / 기여도</div>
          <ss.TechHolder>
            {data.jobs.map((item, idx) => {
              return (
                <ContributionChart
                  key={"chart" + idx}
                  className="techHolder__chart"
                  jobName={item.job_name}
                  myJob={item.my_job}
                  withTeam={item.with_team}
                  percent={item.percentage}
                />
              );
            })}
          </ss.TechHolder>
        </ss.TechContainer>

        <div className="subContainer">
          <ss.TextHolder>
            <div className="title">
              <div className="bar_start" />
              <div className="text">유니티 WebGL 프로젝트</div>
              <div className="bar_end" />
            </div>
            <div className="desc">
              <div>
                프로젝트를 유니티의 WebGL로 빌드하여 웹에 게시하였습니다.
                <br />
                터치 조이스틱을 이용해 미로에 있는 꽃 속의 예술을 만나는 순서로
                진행됩니다.
              </div>
            </div>
          </ss.TextHolder>
        </div>
        <ss.ImageHolder>
          <ss.Holder__video
            className="holder__video"
            autoPlay
            muted
            loop
            controls={false}
            src={videoUrls[0]}
            column={"3 / 8 span"}
          />
          <ss.Holder__image
            src={imagesUrls[1]}
            className="holder__image"
            alt="img01"
            column={"3 / 8 span"}
            objPos="0% 0"
          />
        </ss.ImageHolder>

        <div className="subContainer">
          <ss.TextHolder>
            <div className="title">
              <div className="bar_start" />
              <div className="text">꽃 컨셉에 충실하게</div>
              <div className="bar_end" />
            </div>
            <div className="desc">
              <div>
                중심 컨셉이 꽃이였기 때문에 꽃의 이미지를 주요하게
                사용하였습니다.
                <br />
                꽃 모양으로 공연예술의 세가지 분류를 표현했습니다. 영상을 본 뒤
                팝업을 닫으면 꽃잎을 흩뿌려 정적인 공간에 부드러움을 더하고자
                했습니다.
                <br />
                팝업창에 꽃 손그림 이미지를 배치하여 공연예술의 분류를 제시,
                동시에 풍성한 이미지를 연출했습니다.
              </div>
            </div>
          </ss.TextHolder>
        </div>

        <ss.ImageHolder>
          <ss.Holder__image
            src={imagesUrls[2]}
            className="holder__image"
            column={"3 / 8 span"}
            alt="img01"
            objPos="0% 0"
          />
          <ss.Holder__image
            src={imagesUrls[5]}
            className="holder__image"
            column={"3 / 8 span"}
            alt="img02"
          />
          <ss.Holder__image
            src={imagesUrls[4]}
            className="holder__image"
            column={"3 / 4 span"}
            alt="img03"
          />
          <ss.Holder__image
            src={imagesUrls[3]}
            className="holder__image"
            column={"7 / 4 span"}
            alt="img04"
          />
        </ss.ImageHolder>

        <div className="subContainer">
          <ss.TextHolder>
            <div className="title">
              <div className="bar_start" />
              <div className="text">프로젝트 종료 후</div>
              <div className="bar_end" />
            </div>
            <div className="desc">
              <div>
                유니티의 멀티 플랫폼 디버깅 기능이 좋다 말만들었지 진짜로
                뛰어든건 처음이였기 때문에 굉장히 많이 헤맸습니다. 안드로이드
                기준으로 작업한 내용이 iOS에서는 전혀 동작하지 않는다던지 하는
                호환성 문제 때문에 낮은 WebGL 버전을 사용할 수 밖에 없었습니다.
                버전을 낮추게 되니 유니티의 Global Illumination 렌더러에도
                한계가 생겨 더 꾸미지 못한게 아쉬웠습니다.
              </div>
            </div>
          </ss.TextHolder>
        </div>
      </ss.MainContainer>
    </>
  );
}
