import React from "react";
import { Link, useLocation, useNavigate } from "react-router-dom";
import styled from "styled-components";

const DL = styled(Link)`
  width: 100vw;
  height: 100vh;
`;

export default function DelayLink(props) {
  const { delay, onDelayStart, onDelayEnd, replace, to } = props;
  let timeout = null;
  let location = useLocation();
  let navigate = useNavigate();

  // useEffect(() => {
  //   // 언마운트시 타임아웃 정리
  //   return () => {
  //     if (timeout) {
  //       clearTimeout(timeout);
  //     }
  //   };
  // });

  const handleClick = (e) => {
    // location 인풋이 목적지와 같으면 반환
    if (location?.pathname === to) return;

    // onDelayStart(e, to);

    if (e.defaultPrevented) {
      return;
    }

    e.preventDefault();

    // delay만큼 대기
    timeout = setTimeout(() => {
      // to로 이동
      if (replace) {
        navigate(to, { replace: true });
      } else {
        navigate(to);
      }
      // onDelayEnd(e, to);
    }, delay);
  };

  DelayLink.defaultProps = {
    replace: false,
    delay: 0,
    onDelayStart: () => {},
    onDelayEnd: () => {},
  };

  return <DL to={to} onClick={handleClick} />;
}
