import React, { Suspense, useState, useEffect } from "react";
import { Route, Switch, useLocation } from "react-router-dom";
import { Welcome, About, Works } from "../pages/";
import { AnimatePresence, motion } from "framer-motion";
import { GlobalStyle } from "../pages/sharedStyle";

function onLoadTest() {
  console.log("loaded!");
}

const App = () => {
  const [loading, setLoading] = useState(false);
  const location = useLocation();
  useEffect(() => {
    console.log("loading");
  }, [loading]);

  return (
    <AnimatePresence exitBeforeEnter>
      <Suspense fallback={<h1>Loading pages..</h1>}>
        <GlobalStyle />
        <Switch location={location} key={location.pathname}>
          <Route exact path={["/pyp", "/"]} component={Welcome} />
          {loading ? <h1>Loading pages..</h1> : null}
          <Route path="/about" component={About} onLoad={setLoading(true)} />
          <Route path="/works" component={Works} onLoad={setLoading(true)} />
        </Switch>
      </Suspense>
    </AnimatePresence>
  );
};

export default App;
