import React, { useState, useEffect } from "react";
import { ss } from "..";
import JSON_DATA from "../../Data3.json";

import Header from "../../components/Header";
import IntroHolder from "../../components/IntroHolder";
import ContributionChart from "../../components/ContributionChart";
import LinkButton from "../../components/Button";

export default function Doh({ match, history }) {
  // JSON 파일을 만들어서 ContentsId랑 일치하는 오브젝트 불러오기
  const contentsId = match.params.contentsId;
  const [isScrolled, setScrollActive] = useState(false);
  var data = null;
  var imagesUrls = [];
  var imagesObjectUrls = [];
  var videoUrls = [];

  window.scrollTo(0, 0);

  JSON_DATA.map((item) => {
    if (item.id == contentsId) {
      data = item;
      return;
    }
    return <div>Somethings wrong!</div>;
  });

  // 이미지+public 폴더 주소 합치기
  data.images.map((item) => {
    //배열 형태에 전달
    imagesUrls.push(process.env.PUBLIC_URL + item);

    //오브젝트 형태에 전달 <- 이미지갤러리 등에 쓰는 경우
    imagesObjectUrls.push({ original: process.env.PUBLIC_URL + item });
    return;
  });

  // 비디오+public 폴더 주소 합치기
  data.videos.map((item) => {
    videoUrls.push(process.env.PUBLIC_URL + item);
    return;
  });

  return (
    <>
      <Header nav={"WORKS"} />
      <ss.HeadContainer textColor="white">
        <ss.HeadContainerImg imageUrl={data.link_banner} />
        {/* <ss.BackBtn onClick={() => history.goBack()}>뒤로</ss.BackBtn> */}
        <div className="titleText Holder">
          <div className="01">Dancing on the Hills</div>
        </div>
      </ss.HeadContainer>

      <ss.Background>
        <IntroHolder
          title={data.title}
          client={data.client}
          period={data.period}
          subtitle={data.subtitle}
          team={data.team}
          results={data.results.map((item, idx) => {
            return (
              <span key={idx}>
                {item}
                <br />
              </span>
            );
          })}
        />

        <ss.MainContainer className={isScrolled ? "scrolled" : ""}>
          {/* SVG 이미지 로드 */}
          <ss.SvgImg
            type="image/svg+xml"
            data={process.env.PUBLIC_URL + "/img/svg/aboutCircle.svg"}
            top="14%"
            left="-20%"
          />

          <div className="subContainer">
            <ss.TextHolder first={true}>
              <div className="title">
                <div className="bar_start" />
                <div className="text">멋져보여서 시작한 프로젝션 매핑 작업</div>
              </div>

              <div className="desc">
                <div>
                  학부생일 당시 시도해본 프로젝션 매핑 프로젝트입니다.
                  <br />
                  하고싶은걸 해보자라는 생각으로 시작했습니다. 점-선-면의
                  시각화를 중심 컨셉으로 잡았습니다.
                  <br />
                  점-선-면이 대상물 위에서 춤을 추는 것처럼 보여 제목을 "Dancing
                  on the hills"로 지었습니다.
                  <br />
                  대상물을 제작해서 빔프로젝터로 영상을 투사했습니다.
                </div>
              </div>
            </ss.TextHolder>
          </div>

          <ss.TechContainer>
            <div className="techHolder__title">담당업무 / 기여도</div>
            <ss.TechHolder>
              {data.jobs.map((item, idx) => {
                return (
                  <ContributionChart
                    key={"chart" + idx}
                    className="techHolder__chart"
                    jobName={item.job_name}
                    myJob={item.my_job}
                    withTeam={item.with_team}
                    percent={item.percentage}
                  />
                );
              })}
            </ss.TechHolder>
          </ss.TechContainer>

          <div className="subContainer">
            <ss.TextHolder>
              <div className="title">
                <div className="bar_start" />
                <div className="text">점-선-면을 표현해보자</div>
                <div className="bar_end" />
              </div>
              <div className="desc">
                <div>
                  배경음의 박자에 맞춰 발랄한 움직임들을 사용했습니다.
                  <br />
                  지루하지 않게 최대한 다양한 표현을 하려고 노력했습니다.
                  <br />
                  영상 중반부에 동기생들의 작업물 일부를 넣어 과제전의 인트로
                  영상으로 사용했습니다.
                </div>
              </div>
            </ss.TextHolder>
          </div>
          <ss.ImageHolder>
            <ss.Holder__iframe column={"3 / 8 span"}>
              <iframe
                width="100%"
                height="800"
                src="https://www.youtube.com/embed/L2ln5hDMPJM"
                title="DOH"
                rel="0"
                frameBorder="0"
                allow="accelerometer; encrypted-media; gyroscope"
                allowFullScreen
              ></iframe>
            </ss.Holder__iframe>

            <ss.Holder__image
              src={imagesUrls[0]}
              className="holder__image"
              alt="img00"
              column={"3 / 8 span"}
            />
            <ss.Holder__image
              src={imagesUrls[1]}
              className="holder__image"
              alt="img01"
              column={"3 / 8 span"}
            />
            <ss.Holder__image
              src={imagesUrls[6]}
              className="holder__image"
              alt="img02"
              column={"3 / 8 span"}
            />
            <ss.Holder__image
              src={imagesUrls[2]}
              className="holder__image"
              alt="img02"
              column={"3 / 4 span"}
            />
            <ss.Holder__image
              src={imagesUrls[3]}
              className="holder__image"
              alt="img03"
              column={"7 / 4 span"}
            />
            <ss.Holder__image
              src={imagesUrls[7]}
              className="holder__image"
              alt="img00"
              column={"3 / 8 span"}
            />
          </ss.ImageHolder>

          <div className="subContainer">
            <ss.TextHolder>
              <div className="title">
                <div className="bar_start" />
                <div className="text">프로젝트 종료 후</div>
                <div className="bar_end" />
              </div>
              <div className="desc">
                <div>
                  직접 만든 프로젝션 매핑이라 재미있었습니다.
                  <br />
                  작업이 예상한것 보다 더 손이 많이 갔지만, 아직까지도 좋은
                  기억으로 남아있습니다.
                  <br />
                  대상물의 형태를 조금 더 적극적으로 이용했으면 좋았을 것 같은
                  아쉬움이 남습니다.
                </div>
              </div>
            </ss.TextHolder>
          </div>
        </ss.MainContainer>
      </ss.Background>
    </>
  );
}
