import React from "react";
import { Link } from "react-router-dom";
import styled from "styled-components";

const ThumbImg = styled.figure`
  max-width: 25%;

  img {
    width: 100%;
    cursor: pointer;
  }

  & > a > .categoryHolder {
    position: absolute;
    right: 0px;
    width: 100px;
    height: 100px;
    border: red 2px solid;
  }
`;

const TextWrapperContainer = styled.div``;

const ItemCells = styled.div`
  /* position: relative; */
  border-top: 2px solid;
  border-bottom: 2px solid;
  margin-bottom: -2px;

  display: flex;
  padding-top: 4vh;
  padding-bottom: 4vh;
  box-sizing: border-box;
  filter: grayscale(100%);

  transition: 0.5s;

  &.underConstruction {
    pointer-events: none;
  }

  &:hover {
    filter: grayscale(0%);
  }

  & > .panel {
    width: 100%;
    height: 100%;
    position: absolute;
    background-color: black;
  }

  ${TextWrapperContainer} {
    display: flex;
    flex: 1;
    padding-left: 4%;
    flex-flow: column;

    & > .titleContainer {
      max-width: 67%;
      display: flex;
      justify-content: space-between;
      line-height: 4.5rem;
      margin-bottom: 1rem;
      font-size: 3.5rem;
      font-weight: 500;

      & > a {
        &::after {
          position: absolute;
          right: 3rem;
          opacity: 0;
          transition: 0.3s;
          flex: 0.2;
          content: "--›";
        }

        &:hover:after {
          opacity: 1;
        }
      }
    }

    & > .descContainer {
      max-width: 60%;
      margin-bottom: 1rem;
      font-size: 1.1rem;
      font-weight: 300;
    }

    @media screen and (max-width: 1080px) {
      & > .titleContainer {
        line-height: initial;
        font-size: 2rem;
        font-weight: 500;
      }
    }
  }

  @media screen and (max-width: 1080px) {
    filter: none;

    width: 100%;
    padding-left: 3%;
    padding-right: 3%;
  }
`;

export default function ItemCell(props) {
  return (
    <ItemCells className={props.isConstruction ? "underConstruction" : ""}>
      <ThumbImg>
        <Link to={`${props.path}/${props.alt}`}>
          <div className="categoryHolder"></div>
          <img alt={props.alt} src={props.thumbImg} />
        </Link>
      </ThumbImg>
      <TextWrapperContainer>
        <div className="titleContainer">
          <Link to={`${props.path}/${props.alt}`}>
            <span>{props.title}</span>
          </Link>
        </div>
        <div className="descContainer">
          <span>{props.desc}</span>
        </div>
        <div className="category">
          <span>
            <strong>Category: </strong>
            {props.subtitle}
          </span>
        </div>
      </TextWrapperContainer>
    </ItemCells>
  );
}
