import React, { Component } from "react";
import { Link } from "react-router-dom";
import styled, { css, createGlobalStyle } from "styled-components";

// import "./Welcome.css";

const GlobalStyle = createGlobalStyle`
  body {
    box-sizing: content-box;
    overflow: hidden;
    width: 98vw;
    height: 96vh;
    background-color: white;
    outline: 5vh solid black;
  }

  a {
    text-decoration: none;
    color: teal;
  }

  a:visited {
    color: teal;
    text-underline-position: unset;
  }

  a:hover {
    color: rgb(255, 196, 0);
  }

  p {
    margin: 0
  }
`;

const Title = styled.div`
  text-align: center;
  margin: 0;
  margin-bottom: 1vh;
  font-size: 5em;
  color: black;
`;

const Container = styled.div`
  position: absolute;
  top: 30%;
  left: 50%;  
`

const LinkContainer = styled.div`
  display: flex;
  flex-wrap: wrap;    
  justify-content: center;
`

const Contents = styled.div`
  position: relative;
  left: -50%;

`

const Slink = styled(Link)`
  margin: 2vh 2vh;  
  font-size: 1.5rem;
  font-weight: bold;
  display: inline-block;
`


class Welcome extends Component {
  render() {
    console.log("welcome page rendered");

    return (
      <Container>
        <GlobalStyle />

        <Contents>
          <Title>Pyoeng</Title>
          <LinkContainer className="link-container">
            <Slink to="./About" className="menu-btn" id="link-about">
              About
            </Slink>
            {/* <Link to="./Works" className="menu-btn" id="link-works">
              Works
            </Link> */}
            <a
              className="menu-btn"
              id="link-works"
              onClick={function (e) {
                console.log(e.target);
              }.bind(this)}
            >
              Works
            </a>
          </LinkContainer>
        </Contents>
      </Container>
    );
  }
}

export default Welcome;
