import React, { useState, useEffect } from "react";
import styled, { css } from "styled-components";
import { ss } from ".";
import JSON_DATA from "../Data2.json";
import Header from "../components/Header";

import ImageGallery from "react-image-gallery";
import gallStyle from "./Gallery.css";

export default function Coc2016({ match, history }) {
  // JSON 파일을 만들어서 ContentsId랑 일치하는 오브젝트 불러오기
  const contentsId = match.params.contentsId;
  const [isScrolled, setScrollActive] = useState(false);
  const [backType, setBackType] = useState("img");
  var textData = {};
  var data = null;
  var imagesUrls = [];
  var videoUrls = [];

  window.scrollTo(0, 0);

  JSON_DATA.map((item) => {
    // JSON 오브젝트 나누기
    const [keys, entryValues] = Object.entries(item);
    keys[1].map((element) => {
      if (element.id == contentsId) {
        textData[0] = element;
        return;
      }
    });
  });
  //이미지 배열의 형태 변경
  data = textData[0];

  data.images.map((item) => {
    imagesUrls.push({ original: process.env.PUBLIC_URL + item });
    return;
  });

  data.videos.map((item) => {
    videoUrls.push(process.env.PUBLIC_URL + item);
    return;
  });

  useEffect(() => {
    setBackType(data.banner_type);
  });

  //Render
  return (
    <>
      <Header nav={"WORKS"} />
      <ss.HeadContainer>
        <ss.HeadContainerVid
          autoPlay
          muted
          controls={false}
          loop
          bannerType={data.banner_type}
        >
          <source
            src={process.env.PUBLIC_URL + data.link_banner}
            type="video/mp4"
          ></source>
        </ss.HeadContainerVid>
        <ss.BackBtn onClick={() => history.goBack()}>뒤로</ss.BackBtn>
        <ss.HeaderTextHolder>
          <div className="title">{data.title}</div>
          <div className="subtitle">{data.subtitle}</div>
          <div className="desc">{data.period}</div>
        </ss.HeaderTextHolder>
      </ss.HeadContainer>

      <ss.MainContainer
        className={isScrolled ? "scrolled" : ""}
        paddingTop="20rem"
      >
        <ss.SvgImg
          type="image/svg+xml"
          data={process.env.PUBLIC_URL + "/img/svg/aboutCircle.svg"}
          top="230%"
          left="-20%"
        />

        <div className="subContainer">
          <ss.TextHolder first={true}>
            <div className="title">
              <div className="bar_start" />
              <div className="text">다양한 경험이 가능했던 융합 프로젝트</div>
              <div className="bar_end" />
            </div>
            <div className="desc">
              <div>
                한국전파진흥원 주관, 창의+융합형 인재 육성을 위해 디자인, 예술,
                ICT등 다학계 분야 연구원들이 모인 개방형 캠퍼스 프로젝트입니다.
                <br></br>
                유니티를 이용하여 가상 환경을 생성하고 빔프로젝터를 이용해
                공간에 투사한 뒤, 가상 환경을 제어할 수 있는 테이블을
                제작하였습니다.
                <br></br>
                가상 환경은 외부 센서의 데이터를 이용해 공간을 변경해줍니다.
                <br></br>
                사용자의 현재 감성을 담은 현재 데이터를 측정하여 환경을 감성
                상태에 알맞게 변경해줍니다.
                <br></br>
                <br></br>
                '프로젝트의 설계부터 구현에 이르는 다학제적 융합인재 양성'의
                목적 아래 큰 전공분야의 구분없이 거의 모든 일을 했습니다.
                <br></br>
                전체 내용 기획을 시작으로, 공간에 영상을 투사할 다수의 프로젝터
                제어, UX를 고려한 인터페이스 구성 및 소스 제작, Unity3D를 이용한
                환경 개발, 테이블과 함께 사용한 외부 하드웨어들에서 들어오는
                데이터를 이용한 환경 제어 등을 맡아서 진행하였습니다.
              </div>
            </div>
            <video controls muted className="items" src={videoUrls[0]} />
          </ss.TextHolder>

          <ImageGallery
            style={gallStyle}
            items={imagesUrls}
            showFullscreenButton={false}
            showPlayButton={false}
            showBullets={true}
          />
        </div>
        <ss.TechHolder>
          <ss.TechHolderTitle>담당업무 / 기여도</ss.TechHolderTitle>
          <ss.TechHolderDesc>
            {data.my_job.map((item, idx) => {
              const [key, val] = Object.entries(item);

              return (
                <ss.TechContainer key={idx}>
                  <div className="tech-tmb"></div>
                  <div className="tech-title">{key[0]}</div>
                </ss.TechContainer>
              );
            })}
          </ss.TechHolderDesc>
        </ss.TechHolder>
        <div className="subContainer">
          <ss.ColumnTitle>결과</ss.ColumnTitle>
          <ss.TextHolder>
            <ss.HolderTitle>자체 평가</ss.HolderTitle>
            <ss.HolderDesc>{data.earned}</ss.HolderDesc>
          </ss.TextHolder>
        </div>
      </ss.MainContainer>
    </>
  );
}
