import React from "react";
import Header from "../components/Header";
import styled from "styled-components";

//Import Components
import CategoryTitleDiv from "../components/CategoryTitleDiv";
import ItemCell from "../components/ItemCell";

//Import Style
import { MainContainer } from "./sharedStyle";

//Import JSON data
import JSON_DATA from "../Data.json";
import { render } from "react-dom";
// import JSON_DATA from "../Data2.json";

const CategoryContainer = styled.div`
  float: left;
  width: 100%;
  margin-bottom: 5vh;
`;

function getJsonData(JSON, path) {
  const catList = ["UI/UX", "Graphics", "Web"];
  const uiData = JSON.filter((item) => item.category == "UI/UX");

  for (const cat of catList) {
    render(<CategoryTitleDiv key={"ct"} desc={cat} />);

    for (const item of uiData) {
      console.log("uiData item= " + item.id);
      return (
        <ItemCell
          key={item.id}
          path={path}
          alt={item.id}
          thumbImg={process.env.PUBLIC_URL + item.thumb_img}
          title={item.title}
          category={item.description}
        />
      );
    }
  }
}

export default function WorksList({ match }) {
  const path = match.path;

  window.scrollTo(0, 0);
  var categoryName = null;
  var innerDataArray = null;

  return (
    <>
      <Header nav="Works" />

      <MainContainer>
        <div className="list">
          <CategoryContainer key={"cc"}>
            {getJsonData(JSON_DATA, path)}
          </CategoryContainer>
        </div>
      </MainContainer>
    </>
  );
}

