import JSON_DATA from "../../Data3.json";

import Header from "../../components/Header";
import IntroHolder from "../../components/IntroHolder";
import ContributionChart from "../../components/ContributionChart";

export default function Viaf2021({ match, history }) {
  // JSON 파일을 만들어서 ContentsId랑 일치하는 오브젝트 불러오기
  const contentsId = match.params.contentsId;
  const [isScrolled, setScrollActive] = useState(false);
  var data = null;
  var imagesUrls = [];
  var imagesObjectUrls = [];
  var videoUrls = [];

  window.scrollTo(0, 0);

  JSON_DATA.map((item) => {
    if (item.id == contentsId) {
      data = item;
      return;
    }
    return <div>Somethings wrong!</div>;
  });

  // 이미지+public 폴더 주소 합치기
  data.images.map((item) => {
    //배열 형태에 전달
    imagesUrls.push(process.env.PUBLIC_URL + item);

    //오브젝트 형태에 전달 <- 이미지갤러리 등에 쓰는 경우
    imagesObjectUrls.push({ original: process.env.PUBLIC_URL + item });
    return;
  });

  // 비디오+public 폴더 주소 합치기
  data.videos.map((item) => {
    videoUrls.push(process.env.PUBLIC_URL + item);
    return;
  });

  return (
    <>
      <Header nav={"WORKS"} />
      <ss.HeadContainer>
        <ss.HeadContainerImg imageUrl={data.link_banner} />
        {/* <ss.BackBtn onClick={() => history.goBack()}>뒤로</ss.BackBtn> */}
        <div className="titleText Holder">
          <div className="01">VIAF 2021</div>
        </div>
      </ss.HeadContainer>

      <IntroHolder
        title={data.title}
        client={data.client}
        period={data.period}
        subtitle={data.subtitle}
        team={data.team}
        results={data.results.map((item) => {
          return (
            <>
              {item}
              <br />
            </>
          );
        })}
      />

      <ss.MainContainer className={isScrolled ? "scrolled" : ""}>
        {/* SVG 이미지 로드 */}
        <ss.SvgImg
          type="image/svg+xml"
          data={process.env.PUBLIC_URL + "/img/svg/aboutCircle.svg"}
          top="200%"
          left="-20%"
        />

        <div className="subContainer">
          <ss.TextHolder first={true}>
            <div className="title">
              <div className="bar_start" />
              <div className="text">가상의 미로를 탐방하는 웹 인터랙티브</div>
              {/* <div className="bar_end" /> */}
            </div>
            <div className="desc">
              <div>
                '길(VIA)'위에서 펼쳐져 온 비아페스티벌(VIA Festival)은 2021년,
                코로나로 인한 시대적 상황에 따라 온라인 축제로 진행하게
                되었습니다. 예술가들의 선택에 따른 공연 환경의 특성을 존중하여
                예술과 기술이 결합된 디지털 환경 속, 가상의 길 위에서의 축제
                콘텐츠를 향유하도록 계획하였습니다. 특히, 일반적인 온라인 축제의
                나열식 배치에서 벗어나 미로형태의 '길'을 구현함과 동시에
                '게임'형식을 접목하여 예술 작품을 감상하기 위한 과정을
                재현하였습니다. ​만인이 공유하는 공간, '길'위에서 보다 많은
                동시대인들이 예술이라는 '꽃'을 향유할 수 있도록 한다는 기본적인
                방향성을 잃지 않는 가운데, 2021 비아페스티벌 온라인이
                예술가에게는 새로운 창작의 지속 가능성을 그리고 관객들에게는
                새로운 시공간에서의 감상의 기회를 제공하기를 기대합니다. 처음엔
                단순 웹디자인이였는데, 일이 커져서 WebGL까지 만들게 됐습니다.
              </div>
            </div>
            {/* COC 가이드 비디오 */}
            <video
              // style={{ pointerEvents: "none" }}
              muted
              controls
              className="items"
              src={videoUrls[1]}
            />
          </ss.TextHolder>
        </div>
        <div className="subContainer">
          <ss.TextHolder>
            <div className="title">
              <div className="bar_start" />
              <div className="text">유니티 WebGL 프로젝트</div>
              <div className="bar_end" />
            </div>
            <div className="desc">
              <div>
                프로젝트를 유니티의 WebGL로 빌드하여 웹에 게시하였습니다.
              </div>
            </div>
          </ss.TextHolder>
        </div>
        <ss.ImageHolder>
          <ss.Holder__video
            className="holder__video"
            autoPlay
            muted
            loop
            controls={false}
            src={videoUrls[0]}
            column={"2 / 10 span"}
          />
          <ss.Holder__image
            src={imagesUrls[2]}
            className="holder__image"
            alt="img02"
            column={"2 / 5 span"}
            objPos="0% 0"
          />
          <ss.Holder__image
            src={imagesUrls[1]}
            className="holder__image"
            alt="img02"
            column={"7 / 5 span"}
          />
          <ss.Holder__image
            src={imagesUrls[3]}
            className="holder__image"
            alt="img01"
            column={"2 / 10 span"}
            objPos="0% 0"
          />
        </ss.ImageHolder>

        <div className="subContainer">
          <ss.TextHolder>
            <div className="title">
              <div className="bar_start" />
              <div className="text">터치 테이블 형태의 사용자 인터페이스</div>
              <div className="bar_end" />
            </div>
            <div className="desc">
              <div>
                대형 테이블에 터치 기능을 구현했기 때문에, 사용자의 손이 닿는
                테이블 하단에 메인 인터페이스를 구성했습니다. 테이블 상단은 현재
                감성상태 등을 확인할 수 있는 대시보드로 사용했습니다.
                <br />
                감성과 표정, 동작인식등 다양한 인식 기능이 인지적 피로를 유발할
                수 있다고 판단하였기에 인터페이스는 최대한 간결하게
                제작했습니다.
              </div>
            </div>
          </ss.TextHolder>
        </div>

        <ss.ImageHolder>
          <ss.Holder__image
            src={imagesUrls[6]}
            className="holder__image"
            column={"2 / 5 span"}
            alt="img01"
            objPos="0% 0"
          />
          <ss.Holder__image
            src={imagesUrls[5]}
            className="holder__image"
            column={"7 / 5 span"}
            alt="img02"
            objPos="90% 0"
          />
        </ss.ImageHolder>

        <div className="subContainer">
          <ss.TextHolder>
            <div className="title">
              <div className="bar_start" />
              <div className="text">프로젝트 관련 결과물 제작</div>
              <div className="bar_end" />
            </div>
            <div className="desc">
              <div>
                프로젝트의 결과물로 책자를 제작/배포하여 결과보고에서 좋은
                점수를 받았습니다.
              </div>
            </div>
          </ss.TextHolder>
        </div>

        <ss.ImageHolder>
          <ss.Holder__image
            src={imagesUrls[7]}
            className="holder__image"
            alt="img02"
            column={"2 / span 10"}
          />
          <ss.Holder__image
            src={imagesUrls[8]}
            className="holder__image"
            alt="img02"
            column={"2 / span 5"}
          />
          <ss.Holder__image
            src={imagesUrls[9]}
            className="holder__image"
            alt="img02"
            column={"7 / span 5"}
          />
        </ss.ImageHolder>

        <ss.TechContainer>
          <div className="techHolder__title">담당업무 / 기여도</div>
          <ss.TechHolder>
            {data.jobs.map((item, idx) => {
              return (
                <ContributionChart
                  className="techHolder__chart"
                  jobName={item.job_name}
                  myJob={item.my_job}
                  withTeam={item.with_team}
                  percent={item.percentage}
                />
              );
            })}
            {/* <ContributionChart
              className="techHolder__chart"
              myJob={data.jobs.my_job}
              withTeam={data.with_team}
              percent={70}
            /> */}
            {/* <ContributionChart className="techHolder__chart" percent={80} /> */}
          </ss.TechHolder>
        </ss.TechContainer>

        {/* <div className="subContainer">
          <ss.ColumnTitle>결과</ss.ColumnTitle>
          <ss.TextHolder>
            <ss.HolderTitle>자체 평가</ss.HolderTitle>
            <ss.HolderDesc>{data.earned}</ss.HolderDesc>
          </ss.TextHolder>
        </div> */}
      </ss.MainContainer>
    </>
  );
}
