import React, { useState, useEffect, useRef } from "react";
import { Link } from "react-router-dom";

import { GlobalStyle, MainContainer } from "./sharedStyle";
import styled from "styled-components";
import { useScroll } from "./Works__/CustomHooks";

import { Footer } from "../components";

//Animation API
import AOS from "aos";
import "aos/dist/aos.css";

//#region 간판 스타일

const TextBox = styled.div`
  margin-bottom: 6rem;
  padding-right: 14px;
  word-break: keep-all;
  & > .box__title {
    font-size: 1.2em;
    font-weight: 600;
    margin-bottom: 0.8rem;
  }
`;

const Border = styled.div`
  position: relative;

  width: auto;
  height: 96vh;
  left: 2em;
  right: 2em;
  top: 2em;
  display: content-box;
  z-index: -2;

  @media screen and (max-width: 1080px) {
    left: 1em;
    right: 1em;
    top: 1em;
    bottom: 1em;
  }
`;

const Container = styled.div`
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -75%);
`;

const ScrollDownNotice = styled.div`
  position: absolute;
  width: 50px;
  height: 100px;
  left: 52%;
  top: 80vh;
  transform: translate(-50%, 0);
  z-index: 3;

  transition: 0.2s;
  animation-duration: 1s;
  animation-name: "popLoop";
  animation-iteration-count: infinite;
  animation-direction: alternate;

  & > .arrow {
    fill: #3d3d3d;
    stroke: #3d3d3d;
    stroke-width: 8;
    stroke-linecap: round;
    stroke-linejoin: round;
  }

  &.scrolled {
    opacity: 0;
  }

  @keyframes popLoop {
    from {
      top: 80vh;
    }
    to {
      top: 85vh;
    }
  }
`;

// const LinkContainer = styled.div`
//   display: flex;
//   flex-wrap: wrap;
//   justify-content: center;
// `;

const Contents = styled.div`
  position: relative;
`;

const TitleImg = styled.svg`
  width: 25rem;
  fill: #fff;

  .st0 {
    animation-name: lineAnim;
    animation-duration: 5s;
    animation-timing-function: ease-in-out;
    animation-fill-mode: forwards;

    stroke-width: 2px;
    stroke: black;
    stroke-dasharray: 0 300;
  }

  .circle,
  .poly,
  .rect {
    animation-name: fills;
    animation-duration: 5s;
    animation-timing-function: ease-in-out;
    animation-fill-mode: forwards;
  }

  @keyframes lineAnim {
    to {
      stroke-width: 0px;
      stroke-dasharray: 160 0;
    }
  }

  @keyframes fills {
    to {
      fill: #303030;
      opacity: 1;
    }
  }

  @media screen and (max-width: 1080px) {
    width: 70vw;
  }
`;

//#endregion

//#region About 스타일
const AbMainContainer = styled(MainContainer)`
  /* position: absolute; */
  /* top: 100%; */
  background-color: #f9f9f9;
  margin-top: 5vh;
  height: auto;
  overflow-y: hidden;
  font-size: 1.2rem;
  box-sizing: content-box;
  @media screen and (max-width: 1080px) {
    padding: 0 2%;
    font-size: 1rem;
  }

  & > .main__introImage {
    margin-top: 18vh;
    margin-bottom: 12vh;
    height: auto;
    display: grid;
    grid-template-columns: repeat(8, 1fr);
    grid-template-rows: 50vh;
    grid-auto-rows: max-content;

    @media screen and (max-width: 800px) {
      margin-top: 4vh;
      grid-template-columns: 1fr;
    }

    & > .introContainer {
      &.first {
        grid-column: 2 / span 3;
      }
      &.second {
        grid-column: span 3;
      }
      height: fit-content;
      margin: auto;
      width: 80%;

      & > .circle,
      .polygon {
        margin: auto;
        width: 65%;

        @media screen and (max-width: 1080px) {
          width: 55%;
        }
      }

      & > .circle {
        fill: #ff9f31;
      }

      & > .polygon {
        fill: #6a99ff;
      }

      & > .text {
        text-align: center;
        font-size: 2.3rem;
        font-weight: 500;

        @media screen and (max-width: 1080px) {
          font-size: 1.5rem;
          font-weight: normal;
        }
      }
    }
  }

  & > .text_container {
    max-width: 45rem;
    display: grid;
    grid-template-columns: 1fr;
    width: auto;
    margin: auto;
    margin-top: 5rem;

    & > .horizontal-divider {
      margin: auto;
      margin-top: 100px;
      margin-bottom: 100px;
      width: 100%;
      height: 1px;
      background-color: gray;
    }

    & > .btn {
      justify-self: center;
    }
  }

  & > .about_intro {
    margin: auto;
  }
`;

const TestButton = styled.div`
  width: 100px;
  height: 100px;
  background-color: skyblue;
  position: fixed;
  top: 400px;
  left: 100px;
`;

const WorksButton = styled.button`
  display: inline-block;
  font-size: 1.2rem;
  font-weight: 600;
  width: auto;
  margin: auto;
  padding: 1rem 2rem;
  color: white;
  background-color: #4cc790;
  border: 2px black solid;
  cursor: pointer;
  transition: 0.2s;

  &:hover {
    background-color: #79dfb1;
  }
  &:active {
    background-color: #3f9b72;
  }
`;
//#endregion

/**
 * Function
 */
//#region function
export default function Welcome() {
  const [isScroll, setIsScroll] = useState(false);
  const violationRef = useRef();
  const { scrollY } = useScroll();

  useEffect(() => {
    AOS.init({ once: true });
    console.log("Welcome page rendered.");
    window.scrollTo(0, 0);
  }, []);

  useEffect(() => {
    if (scrollY > 1000) {
      setIsScroll(true);
    } else {
      setIsScroll(false);
    }
  }, [scrollY]);

  return (
    <>
      <ScrollDownNotice className={isScroll ? "scrolled" : "scroll_down"}>
        <svg
