import React from "react";
import { Route, Switch, useLocation } from "react-router-dom";
import { createBrowserHistory } from "history";

import { Welcome, About, Works } from "../pages/";
import { AnimatePresence } from "framer-motion";
import { GlobalStyle } from "../pages/sharedStyle";

const history = createBrowserHistory();

const App = () => {
  const location = useLocation();

  return (
    <>
      <GlobalStyle />
      <AnimatePresence exitBeforeEnter>
        <Switch location={location} key={location.pathname}>
          <Route
            history={history}
            exact
            path={["/pyp", "/"]}
            component={Welcome}
          />
          {/* <Route history={history} path="/about" component={About} /> */}
          <Route history={history} path="/works" component={Works} />
          <Route history={history} component={<div>404 NOT FOUND.</div>} />
        </Switch>
      </AnimatePresence>
    </>
  );
};

export default App;
