import React, { Suspense } from "react";
import { Route, Switch, useLocation } from "react-router-dom";
import { Welcome, About, Works } from "../pages/";
import { AnimatePresence, motion } from "framer-motion";
import { GlobalStyle } from "../pages/sharedStyle";

function wait() {
  setTimeout(() => console.log("NOW LOADING"), 1000);
}

const App = () => {
  wait();
  const location = useLocation();
  return (
    <AnimatePresence exitBeforeEnter>
      <Suspense fallback={<h1> {console.log("LOADING")}Loading pages..</h1>}>
        <GlobalStyle />
        <Switch location={location} key={location.pathname}>
          <Route exact path={["/pyp", "/"]} component={Welcome} />
          <Route path="/about" component={About} />
          <Route path="/works" component={Works} />
        </Switch>
      </Suspense>
    </AnimatePresence>
  );
};

export default App;
