import styled from "styled-components";
import { createGlobalStyle } from "styled-components";
// Shared style

export const colors = {
  primary: "#101010",
  secondary: "#ffffff",
  background: "#f9f9f9",
};

export const GlobalStyle = createGlobalStyle`
  
  html {
    background-color: ${colors.background};
    scroll-behavior: smooth;
    /* padding-right: 14px; */
    margin: 0;
    overflow-x: hidden;
    font-family: 'Noto Sans KR', sans-serif;
    font-size: 16px;
    letter-spacing: -0.05rem;
    word-break: keep-all;

    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    
    ::-webkit-scrollbar {
      width: 14px;
    }

    ::-webkit-scrollbar-thumb {
      border: 4px solid rgba(0, 0, 0, 0);
      background-clip: padding-box;
      border-radius: 9999px;
      background-color: #AAAAAA;
    }

    
    
  }

  a {
    text-decoration: none;
    color: black;
  }

  a:visited {
    color: black;
    text-underline-position: unset;
  }

  a:hover {
    color: rgb(80, 80, 80);
  }
`;

export const HeadContainer = styled.div`
  position: fixed;
  z-index: -4;
  width: 100%;
  height: 100vh;
  padding-top: 4rem;
  /* overflow: hidden; */
  color: ${(props) => props.textColor || "white"};
  text-shadow: 0 0 4px
    ${(props) => (props.textColor === "black" ? "white" : "black")};

  & > .titleText {
    position: absolute;
    top: 45%;
    left: 50%;
    text-align: center;
    letter-spacing: 0.4rem;

    transform: translate(-50%);
    font-size: 3rem;
    font-weight: 700;

    @media screen and (max-width: 1080px) {
      width: 100vh - 1.5rem;
      left: 1rem;
      top: 30%;
      transform: translate(0);

      font-weight: 500;
      text-align: left;
      font-size: 2.5rem;
      letter-spacing: 0;
    }
  }
`;

export const HeadContainerImg = styled.div`
  height: 100%;
  background: no-repeat center/cover;
  background-image: url(${(props) => process.env.PUBLIC_URL + props.imageUrl});
`;

export const HeadContainerVid = styled.video`
  width: auto;
  height: auto;
  min-height: 100%;

  margin: auto;
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translateX(-50%) translateY(-50%);

  -webkit-transform: translateX(-50%) translateY(-50%);
  -moz-transform: translateX(-50%) translateY(-50%);
  -ms-transform: translateX(-50%) translateY(-50%);
  -o-transform: translateX(-50%) translateY(-50%);
`;

export const Background = styled.div`
  position: relative;
  top: 100vh;
  background-color: #f9f9f9;
  height: auto;
  overflow-x: hidden;
`;

export const MainContainer = styled.div`
  width: 100%;
  padding-bottom: 5rem;
  padding-top: ${(props) => props.paddingToTop || 0};

  /* &.scrolled {
    background-color: white;
  } */

  & > .subContainer {
    max-width: 45rem;
    margin-left: auto;
    margin-right: auto;
    margin-top: 10rem;
    margin-bottom: 4rem;

    @media screen and (max-width: 1080px) {
      padding-left: 3%;
      padding-right: 3%;
    }
  }

  & > .itemList {
    max-width: 80rem;
    margin: 0 auto;
  }
`;

export const ColumnTitle = styled.div`
  position: relative;
  left: -1%;
  font-size: 1.5rem;
  display: inline-block;
  width: auto;
  padding: 0 1em 0 0.4em;
  height: 2em;
  line-height: 1.8em;
  margin-bottom: 1em;
  margin-top: ${(props) => (props.first ? "0em" : "4em")};
  background-color: black;
  color: white;
`;

export const HolderTitle = styled.span`
  font-weight: 600;
`;

export const HolderDesc = styled.span`
  white-space: pre-line;
  margin-left: 0px;
  margin-top: 5px;
`;

export const TextHolder = styled.div`
  width: auto;
  display: grid;
  font-size: 1.1rem;
  grid-template-columns: repeat(3, 1fr);
  gap: 2rem 1rem;
  margin-top: ${(props) => (props.first ? "0rem" : "10rem")};

  & > .title {
    position: relative;
    justify-content: left;
    font-size: 1.5rem;
    font-weight: 600;
    grid-column: span 3;

    & > .bar_start {
      position: absolute;
      background-color: black;
      width: 2.5rem;
      height: 5px;
      display: block;
      top: -20px;

      @media screen and (max-width: 1080px) {
        top: -15px;
        width: 2rem;
        height: 3px;
      }
    }
  }

  & > .desc {
    grid-column: span 3;
    line-height: 2rem;
  }

  & > .items {
    max-width: 100%;
    border: black solid 1px;
    grid-column: span 3;
  }

  & > .buttonHolder {
    grid-column: span 3;
  }
`;

export const Holder__div = styled.div`
  position: relative;
  grid-column: ${(props) => props.column || "6 span"};
  grid-row: ${(props) => props.row || null};

  @media screen and (max-width: 1080px) {
    grid-column: 12 span;
  }
`;

export const Holder__image = styled.img`
  object-position: ${(props) => props.objPos || null};
  grid-column: ${(props) => props.column || "6 span"};
  grid-row: ${(props) => props.row || null};

  @media screen and (max-width: 1080px) {
    grid-column: 12 span;
  }
`;

export const Holder__video = styled.video`
  object-position: ${(props) => props.objPos || null};
  grid-column: ${(props) => props.column || "1 / 7"};
  grid-row: ${(props) => props.row || null};

  @media screen and (max-width: 1080px) {
    grid-column: 12 span;
  }
`;

export const Holder__iframe = styled.div`
  grid-column: ${(props) => props.column || "3 / 8"};
  grid-row: ${(props) => props.row || null};

  @media screen and (max-width: 1080px) {
    grid-column: 12 span;
  }
`;

export const ImageHolder = styled.div`
  width: 100%;
  box-sizing: content-box;
  height: auto;
  display: grid;
  grid-template-columns: repeat(12, 1fr);
  gap: 0.15rem;
  margin-bottom: 14rem;
  /* 
  @media screen and (max-width: 1080px) {
    grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  } */

  ${Holder__div} {
    width: 100%;
    height: 100%;
  }

  ${Holder__image} {
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  ${Holder__video} {
    pointer-events: none;
    width: 100%;
    height: 100%;
    object-fit: cover;
  }
`;

export const TechContainer = styled.div`
  z-index: 2;
  background-color: #d7d7d740;
  backdrop-filter: blur(3px);
  padding: 5rem 15% 7rem 15%;
  margin-top: 18rem;
  margin-bottom: 18rem;

  & > .techHolder__title {
    font-size: 1.6em;
    font-weight: 600;
    margin-bottom: 1rem;
    text-align: center;
  }

  @media screen and (max-width: 1080px) {
    padding: 5rem 0 7rem 0;
  }
`;

export const TechHolder = styled.div`
  width: 100%;
  margin: auto;
  margin-top: 2rem;
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  row-gap: 1rem;

  & > .techHolder__chart {
    grid-row-start: 2;
  }
`;

// export const TechContainer = styled.div`
//   width: 20%;
//   height: 10em;
//   background-color: #f5f5f5;
//   border-radius: 15px;
//
//   .tech-tmb {
//     position: relative;
//     top: 10%;
//     left: 50%;
//     transform: translate(-50%);
//     width: 5em;
//     height: 5em;
//     background-color: #757575;
//     border-radius: 5%;
//   }
//   .tech-title {
//     position: relative;
//     text-align: center;
//     top: 13%;
//   }
// `;

export const BackBtn = styled.div`
  position: absolute;
  color: white;
  font-size: 2rem;
  font-weight: 700;
  top: 25%;
  left: 15%;
`;

// img tag for svg
export const SvgImg = styled.object`
  position: absolute;

  top: ${(props) => props.top || "14%"};
  left: ${(props) => props.left || null};
  right: ${(props) => props.right || null};
  width: 35%;
  filter: opacity(0.2);
`;

export const TextBlock = styled.div``;

export const ContentsContainer = styled.div`
  height: auto;
  padding-top: 10em;
  padding-left: 17%;
  padding-right: 17%;
  background-color: gray;

  @media screen and (max-width: 1080px) {
    padding-left: 10%;
    padding-right: 10%;
  }
`;
