import React, { useState, useEffect, Suspense, lazy } from "react";
import {
  Outlet,
  Route,
  Routes,
  useLocation,
  useParams,
} from "react-router-dom";
import { createBrowserHistory } from "history";
import {
  SwitchTransition,
  TransitionGroup,
  CSSTransition,
} from "react-transition-group";
import styled from "styled-components";

import JSON_DATA from "../Data3.json";
import { Header } from "../components";
import { MainContainer } from "../pages/sharedStyle";
import { ItemCell } from "../components";

//Import pages
import {
  Welcome,
  // Works,
  notFound,
  coc2016,
  yng,
  viaf,
  doh,
  gutsy,
  cardivu,
  heartfit,
} from "../pages";
import { GlobalStyle } from "../pages/sharedStyle";
import "./App.css";

// React lazy loading components
const LazyWorks = lazy(() => import("../pages/Works"));
const LazyAbout = lazy(() => import("../pages/Welcome"));

//styled components
const FadeContainer = styled.div`
  &.fadeIn {
    animation: 0.5s fadeIn forwards;
  }

  &.fadeOut {
    animation: 0.5s fadeOut forwards;
  }

  @keyframes fadeIn {
    from {
      opacity: 0;
      transform: translate(-20px, 0);
    }
    to {
      opacity: 1;
      transform: translate(0px, 0px);
    }
  }

  @keyframes fadeOut {
    from {
      opacity: 1;
      transform: translate(0px, 0px);
    }
    to {
      transform: translate(-20px, 0);
      opacity: 0;
    }
  }
`;

const App = () => {
  const location = useLocation();
  const [displayLocation, setDisplayLocation] = useState(location);
  const [transitionStage, setTransitionStage] = useState("fadeIn");

  useEffect(() => {
    if (location != displayLocation) setTransitionStage("fadeOut");
    console.log("locationChanged!");
  }, [location]);

  return (
    <>
      {/* <FadeContainer
        className={`${transitionStage}`}
        onAnimationEnd={() => {
          if (transitionStage === "fadeOut") {
            setTransitionStage("fadeIn");
            setDisplayLocation(location);
          }
        }}
      >
       
      </FadeContainer> */}
      <GlobalStyle />
      <Suspense fallback={<div>Loading!!</div>}>
        <Routes location={location}>
          <Route path="" element={<LazyAbout />} />
          {/* <Route path="" element={<Welcome />} /> */}
          <Route path="works/*" element={<LazyWorks />} />
          {/* <Route path="works/*" element={<WorksList />} /> */}
          <Route path="works/:contentsId" element={<ContentsLoader />} />
        </Routes>
      </Suspense>
      {/* <AnimatePresence exitBeforeEnter>
      </AnimatePresence> */}
    </>
  );
};

