import React, { useState, useEffect } from "react";
import styled, { css } from "styled-components";
import { ss } from "..";
// import JSON_DATA from "../../Data2.json";
import JSON_DATA from "../../Data3.json";

import Header from "../../components/Header";
import IntroHolder from "../../components/IntroHolder";
import ContributionChart from "../../components/ContributionChart";

export default function Yng({ match, history }) {
  // JSON 파일을 만들어서 ContentsId랑 일치하는 오브젝트 불러오기
  const contentsId = match.params.contentsId;
  const [isScrolled, setScrollActive] = useState(false);
  var data = null;
  var imagesUrls = [];
  var imagesObjectUrls = [];
  var videoUrls = [];

  window.scrollTo(0, 0);

  JSON_DATA.map((item) => {
    if (item.id == contentsId) {
      data = item;
      return;
    }
    return <div>Somethings wrong!</div>;
  });

  // 이미지+public 폴더 주소 합치기
  data.images.map((item) => {
    //배열 형태에 전달
    imagesUrls.push(process.env.PUBLIC_URL + item);

    //오브젝트 형태에 전달 <- 이미지갤러리 등에 쓰는 경우
    imagesObjectUrls.push({ original: process.env.PUBLIC_URL + item });
    return;
  });

  // 비디오+public 폴더 주소 합치기
  data.videos.map((item) => {
    videoUrls.push(process.env.PUBLIC_URL + item);
    return;
  });

  return (
    <>
      <Header nav={"WORKS"} />
      <ss.HeadContainer textColor="black">
        <ss.HeadContainerImg imageUrl={data.link_banner} />
        {/* <ss.BackBtn onClick={() => history.goBack()}>뒤로</ss.BackBtn> */}
        <div className="titleText Holder">
          <div className="01">연애능력고사</div>
        </div>
      </ss.HeadContainer>

      <IntroHolder
        title={data.title}
        client={data.client}
        period={data.period}
        subtitle={data.subtitle}
        team={data.team}
        results={data.results.map((item, idx) => {
          return (
            <span key={idx}>
              {item}
              <br />
            </span>
          );
        })}
      />

      <ss.MainContainer className={isScrolled ? "scrolled" : ""}>
        {/* SVG 이미지 로드 */}
        <ss.SvgImg
          type="image/svg+xml"
          data={process.env.PUBLIC_URL + "/img/svg/aboutCircle.svg"}
          top="200%"
          left="-20%"
        />

        <div className="subContainer">
          <ss.TextHolder first={true}>
            <div className="title">
              <div className="bar_start" />
              <div className="text">얼굴 인식으로 알아보는 연애능력</div>
              {/* <div className="bar_end" /> */}
            </div>
            <div className="desc">
              <div>
                간단한 심리검사와 표정 짓기, 표정 알아맞추기 능력을 연애능력
                점수로 환산해줍니다.
                <br />
                (진짜로 연애능력과 연결되지는 않습니다)
              </div>
            </div>
          </ss.TextHolder>
        </div>

        <div className="subContainer">
          <ss.TextHolder>
            <div className="title">
              <div className="bar_start" />
              <div className="text">연애의 색을 컨셉으로 제작</div>
              <div className="bar_end" />
            </div>
            <div className="desc">
              <div>
                사랑스러운 이미지를 위해 분홍색을 주색으로 잡았습니다.
                <br />
                주어진 작업 수행시간이 짧았기 때문에 각 단계마다 안내 영상을
                제작하여 오류를 줄이려 했습니다.
                <br />
                인터페이스 에셋을 만들어 XML에도 활용했습니다.
              </div>
            </div>
          </ss.TextHolder>
        </div>
        <ss.ImageHolder>
          <ss.Holder__image
            src={imagesUrls[0]}
            className="holder__image"
            alt="img01"
            column={"3 / 8 span"}
          />
          <ss.Holder__image
            src={imagesUrls[1]}
            className="holder__image"
            alt="img02"
            column={"3 / 8 span"}
          />
          <ss.Holder__video
            className="holder__video"
            autoPlay
            muted
            loop
            controls={false}
            src={videoUrls[0]}
            column={"3 / 8 span"}
          />
        </ss.ImageHolder>

        <div className="subContainer">
          <ss.TextHolder>
            <div className="title">
              <div className="bar_start" />
              <div className="text">XML로 빚은 인터페이스</div>
              <div className="bar_end" />
            </div>
            <div className="desc">
              <div>
                XML을 이용한 Windows application의 UI 제작은 처음이였는데,
                편의성이 굉장히 뛰어나 놀랐습니다.
                <br />첫 사용이였는데도 금방 익힐 수 있었고, 생각한대로 구현이
                되는 것이 재밌었습니다.
              </div>
            </div>
          </ss.TextHolder>
        </div>

        <ss.ImageHolder>
          <ss.Holder__image
            src={imagesUrls[2]}
            className="holder__image"
            column={"2 / 5 span"}
            alt="img01"
          />
        </ss.ImageHolder>

        <div className="subContainer">
          <ss.TextHolder>
            <div className="title">
              <div className="bar_start" />
              <div className="text">프로젝트 관련 결과물 제작</div>
              <div className="bar_end" />
            </div>
            <div className="desc">
              <div>
                프로젝트의 결과물로 책자를 제작/배포하여 결과보고에서 좋은
                점수를 받았습니다.
              </div>
            </div>
          </ss.TextHolder>
        </div>

        <ss.ImageHolder>
          <ss.Holder__image
            src={imagesUrls[7]}
            className="holder__image"
            alt="img02"
            column={"2 / span 10"}
          />
          <ss.Holder__image
            src={imagesUrls[8]}
            className="holder__image"
            alt="img02"
            column={"2 / span 5"}
          />
          <ss.Holder__image
            src={imagesUrls[9]}
            className="holder__image"
            alt="img02"
            column={"7 / span 5"}
          />
        </ss.ImageHolder>

        <ss.TechContainer>
          <div className="techHolder__title">담당업무 / 기여도</div>
          <ss.TechHolder>
            {data.jobs.map((item, idx) => {
              return (
                <ContributionChart
                  key={"chart" + idx}
                  className="techHolder__chart"
                  jobName={item.job_name}
                  myJob={item.my_job}
                  withTeam={item.with_team}
                  percent={item.percentage}
                />
              );
            })}
          </ss.TechHolder>
        </ss.TechContainer>
      </ss.MainContainer>
    </>
  );
}
