import React, { useState, useEffect } from "react";
import { ss } from "..";
// import JSON_DATA from "../../Data2.json";
import JSON_DATA from "../../Data3.json";

import Header from "../../components/Header";
import IntroHolder from "../../components/IntroHolder";
import ContributionChart from "../../components/ContributionChart";
import LinkButton from "../../components/Button";

export default function Heartfit({ match, history }) {
  // JSON 파일을 만들어서 ContentsId랑 일치하는 오브젝트 불러오기
  const contentsId = match.params.contentsId;
  const [isScrolled, setScrollActive] = useState(false);
  var data = null;
  var imagesUrls = [];
  var imagesObjectUrls = [];
  var videoUrls = [];

  window.scrollTo(0, 0);

  JSON_DATA.map((item) => {
    if (item.id == contentsId) {
      data = item;
      return;
    }
    return <div>Somethings wrong!</div>;
  });

  // 이미지+public 폴더 주소 합치기
  data.images.map((item) => {
    //배열 형태에 전달
    imagesUrls.push(process.env.PUBLIC_URL + item);

    //오브젝트 형태에 전달 <- 이미지갤러리 등에 쓰는 경우
    imagesObjectUrls.push({ original: process.env.PUBLIC_URL + item });
    return;
  });

  // 비디오+public 폴더 주소 합치기
  data.videos.map((item) => {
    videoUrls.push(process.env.PUBLIC_URL + item);
    return;
  });

  return (
    <>
      <Header nav={"WORKS"} />
      <ss.HeadContainer textColor="white">
        <ss.HeadContainerImg imageUrl={data.link_banner} />
        {/* <ss.BackBtn onClick={() => history.goBack()}>뒤로</ss.BackBtn> */}
        <div className="titleText Holder">
          <div className="01">HeartFit</div>
        </div>
      </ss.HeadContainer>

      <IntroHolder
        title={data.title}
        client={data.client}
        period={data.period}
        subtitle={data.subtitle}
        team={data.team}
        results={data.results.map((item, idx) => {
          return (
            <span key={idx}>
              {item}
              <br />
            </span>
          );
        })}
      />

      <ss.MainContainer className={isScrolled ? "scrolled" : ""}>
        {/* SVG 이미지 로드 */}
        <ss.SvgImg
          type="image/svg+xml"
          data={process.env.PUBLIC_URL + "/img/svg/aboutCircle.svg"}
          top="200%"
          left="-20%"
        />

        <div className="subContainer">
          <ss.TextHolder first={true}>
            <div className="title">
              <div className="bar_start" />
              <div className="text">얼굴인식으로 생체정보를 추론하는 앱</div>
            </div>

            <div className="desc">
              <div>
                감성과학센터에서 제작한 생체정보 추론 모바일 어플리케이션의 UI를
                제작/구현했습니다.
                <br />
                앱은 카메라로 얼굴을 인식하여 생체정보를 추론해줍니다.
                생체정보는 기록되고 시각화되어 사용자에게 제공됩니다.
              </div>
            </div>

            <LinkButton
              link="https://play.google.com/store/apps/details?id=com.gamsungtech.heartfit"
              btnText="구글 스토어 링크"
            />
          </ss.TextHolder>
        </div>

        <ss.TechContainer>
          <div className="techHolder__title">담당업무 / 기여도</div>
          <ss.TechHolder>
            {data.jobs.map((item, idx) => {
              return (
                <ContributionChart
                  key={"chart" + idx}
                  className="techHolder__chart"
                  jobName={item.job_name}
                  myJob={item.my_job}
                  withTeam={item.with_team}
                  percent={item.percentage}
                />
              );
            })}
          </ss.TechHolder>
        </ss.TechContainer>

        <div className="subContainer">
          <ss.TextHolder>
            <div className="title">
              <div className="bar_start" />
              <div className="text">XML을 이용한 인터페이스 구성</div>
              <div className="bar_end" />
            </div>
            <div className="desc">
              <div>
                클라이언트에서 요구하는 화면 구성이 있었기 때문에 별다른
                기획단계 없이 제작을 시작했습니다.
                <br />
                얼굴을 인식한 알고리즘이 반환하는 값을 실시간으로 시각화하는
                작업을 진행했습니다.
                <br />
                알고리즘은 몇 가지의 수치값들과 표정분석 결과, 얼굴 정면 여부,
                정보들을 종합한 값을 추론합니다.
                <br />
                추론결과의 종류에 따라 각기 다른 시각화를 제작했습니다.
              </div>
            </div>
          </ss.TextHolder>
        </div>
        <ss.ImageHolder>
          <ss.Holder__image
            src={imagesUrls[3]}
            className="holder__image"
            alt="ui01"
            column={"5 / 4 span"}
          />
          <ss.Holder__image
            src={imagesUrls[1]}
            className="holder__image"
            alt="ui01"
            column={"3 / 8 span"}
          />
          <ss.Holder__image
            src={imagesUrls[2]}
            className="holder__image"
            alt="graphs"
            column={"3 / 8 span"}
          />
        </ss.ImageHolder>

        <div className="subContainer">
          <ss.TextHolder>
            <div className="title">
              <div className="bar_start" />
              <div className="text">프로젝트 종료 후</div>
              <div className="bar_end" />
            </div>
            <div className="desc">
              <div>
                이전까진 Android native에서는 XML을 이용한 UI제작과 단순한
                Java만을 사용했었는데, 이번 기회에 Java를 조금 더 알 수 있어서
                좋았습니다.
                <br />
                프로젝트에 Kotlin을 시도해보고 싶었는데 시간 문제상 뛰어들지
                못한 것이 아쉬움으로 남습니다.
              </div>
            </div>
          </ss.TextHolder>
        </div>
      </ss.MainContainer>
    </>
  );
}
