//       <Header nav="WORKS" />

//       <MainContainer paddingToTop={"10rem"}>
//         <div className="itemList">
//           {/* <div className={`itemList ${isShow ? "show" : "hide"}`}> */}
//           {JSON_DATA &&
//             JSON_DATA.map((item) => {
//               return (
//                 <ItemCell
//                   isConstruction={item.title.includes("공사중")}
//                   key={item.id}
//                   alt={item.id}
//                   thumbImg={process.env.PUBLIC_URL + item.thumb_img}
//                   title={item.title}
//                   subtitle={item.subtitle}
//                 />
//               );
//             })}
//         </div>
//       </MainContainer>
//     </>
//   );
// }

function ContentsLoader() {
  let { contentsId } = useParams();
  console.log("ID: " + contentsId);

  switch (contentsId) {
    case "coc2016":
      return coc2016();

    case "yng":
      return yng();

    case "viaf2021":
      return viaf();

    case "gutsy":
      return gutsy();

    case "heartfit":
      return heartfit();

    case "doh":
      return doh();

    case "cardivu":
      return cardivu();

    default:
      //return 404;
      return (
        <main style={{ padding: "1rem" }}>
          <p>There's nothing here!</p>
        </main>
      );
  }
}

export default App;
