import React from "react";
import styled from "styled-components";

// export const TechContainer = styled.div`
//   z-index: 2;
//   background-color: #b7b7b740;
//   backdrop-filter: blur(5px);
//   padding: 5rem 15% 7rem 15%;

//   @media screen and (max-width: 1080px) {
//     padding: 5rem 1% 7rem 1%;
//   }
// `;

// export const TechHolder = styled.div`
//   width: auto;
//   margin: auto;
//   display: grid;
//   grid-template-columns: repeat(2, auto);
//   grid-template-rows: repeat(3, auto);

//   grid-auto-flow: row dense;
//   row-gap: 5rem;

//   @media screen and (max-width: 1080px) {
//   }
//   & > .techHolder__title {
//     font-size: 1.6em;
//     font-weight: 600;
//     margin-bottom: 1rem;
//     text-align: center;

//     grid-column-start: 1;
//     grid-column-end: 3;
//     grid-row-start: 1;
//     grid-row-end: 2;
//   }

//   /* & > .techHolder__desc {
//     position: absolute;
//     text-align: center;
//     left: 50%;
//     top: 50%;
//   } */

//   & > .techHolder__chart {
//     grid-row-start: 2;
//     grid-row-end: 3;
//   }
// `;

const Chart = styled.svg`
  height: 25vw;

  &.techHolder__chart {
    fill: none;
    stroke: #4cc790;
    stroke-linecap: round;
    stroke-width: 1.8;
    stroke-dasharray: ${(props) => props.percent || 0} 100;
    stroke-dashoffset: -200;
  }

  & > .techHolder__chartbase {
    fill: none;
    stroke: #ddd;
    stroke-width: 1.8;
    stroke-dasharray: 0;
  }
`;

const ChartDesc = styled.div`
  width: auto%;
  position: absolute;
  text-align: center;
  left: 50%;
  top: 50%;
  transform: translate(-50%, -50%);

  @media screen and (max-width: 1080px) {
    position: relative;
    left: 0;
    top: 0;
    transform: translate(0%, 0%);
  }
`;

export default function ContributionChart(props) {
  return (
    <div
      className="ChartContainer"
      style={{ margin: "auto", position: "relative" }}
    >
      <Chart
        className="techHolder__chart"
        percent={props.percent}
        viewBox="0 0 36 36"
      >
        <path
          className="techHolder__chartbase"
          d="M18 2.0845
            a 15.9155 15.9155 0 0 1 0 31.831
            a 15.9155 15.9155 0 0 1 0 -31.831"
        />
        <path
          d="M18 2.0845
            a 15.9155 15.9155 0 0 1 0 31.831
            a 15.9155 15.9155 0 0 1 0 -31.831"
        />
      </Chart>
      <ChartDesc className="techHolder__desc">
        <strong>MY JOB</strong>
        <br></br>
        유니티를 이용한 실시간 가상 환경 구성
        <br></br>
        외부 데이터 통합
        <br></br>
        <br></br>
        <strong>WITH TEAM</strong>
        <br></br>
        외부 센서 하드웨어와 데이터 통신
        <br></br>
        키넥트 동작 인식
        <br></br>
        표정 인식
      </ChartDesc>
    </div>
  );
}
