import React from "react";
import { Link, useLocation, useNavigate } from "react-router-dom";
import { useEffect } from "react/cjs/react.production.min";
import styled from "styled-components";

import DelayLink from "./DelayLink";

const ItemCells = styled(Link)`
  position: relative;
  float: left;
  width: 50%;
  display: block;
  margin-bottom: 8vh;
  padding-left: 1.5%;
  padding-right: 1.5%;
  box-sizing: border-box;
  filter: grayscale(100%);
  cursor: pointer;
  overflow: hidden;

  transition: 0.5s;

  &.underConstruction {
    pointer-events: none;
  }

  & > strong {
    font-size: 2.3rem;
    font-weight: 700;
  }

  & > em {
    font-size: 1.3rem;
    font-weight: 500;
  }

  &:hover {
    filter: grayscale(0%);
  }

  & > .panel {
    width: 100%;
    height: 100%;
    position: absolute;
    background-color: black;
  }

  @media screen and (max-width: 1080px) {
    filter: none;

    width: 100%;
    padding-left: 3%;
    padding-right: 3%;

    & > strong {
      font-size: 2rem;
    }
  }
`;

const ThumbImg = styled.img`
  border: 2px solid black;
  width: 100%;
`;

export default function ItemCell(props) {
  const { delay, delayControl, replace, to } = props;
  let timeout = null;
  let location = useLocation();
  let navigate = useNavigate();

  // useEffect(() => {
  //   // 언마운트시 타임아웃 정리
  //   return () => {
  //     if (timeout) {
  //       clearTimeout(timeout);
  //     }
  //   };
  // });

  // const handleClick = (e) => {
  //   // location 인풋이 목적지와 같으면 반환
  //   if (location?.pathname === to) return;

  //   delayControl(true);

  //   if (e.defaultPrevented) {
  //     return;
  //   }

  //   e.preventDefault();

  //   // delay만큼 대기
  //   timeout = setTimeout(() => {
  //     // to로 이동
  //     if (replace) {
  //       navigate(to, { replace: true });
  //     } else {
  //       navigate(to);
  //     }
  //     delayControl(false);
  //   }, delay);
  // };

  DelayLink.defaultProps = {
    replace: false,
    delay: 0,
    onDelayStart: () => {},
    onDelayEnd: () => {},
  };

  return (
    <ItemCells
      className={props.isConstruction ? "underConstruction" : ""}
      to={`${props.alt}`}
      // onClick={handleClick}
    >
      <ThumbImg alt={props.alt} src={props.thumbImg} />
      <strong>{props.title}</strong> <br />
      <em>{props.subtitle}</em>
    </ItemCells>
  );
}

ItemCell.defaultProps = {
  delay: 0,
  replace: false,
  onDelayStart: () => {},
  onDelayEnd: () => {},
};
