import React, { useState, useEffect } from "react";
import styled, { css } from "styled-components";
import { ss } from "..";
// import JSON_DATA from "../../Data2.json";
import JSON_DATA from "../../Data3.json";

import Header from "../../components/Header";
import IntroHolder from "../../components/IntroHolder";
import ContributionChart from "../../components/ContributionChart";

export default function Coc2016({ match, history }) {
  // JSON 파일을 만들어서 ContentsId랑 일치하는 오브젝트 불러오기
  const contentsId = match.params.contentsId;
  const [isScrolled, setScrollActive] = useState(false);
  var data = null;
  var imagesUrls = [];
  var imagesObjectUrls = [];
  var videoUrls = [];

  window.scrollTo(0, 0);

  JSON_DATA.map((item) => {
    if (item.id == contentsId) {
      data = item;
      return;
    }
    return <div>Somethings wrong!</div>;
  });

  // 이미지+public 폴더 주소 합치기
  data.images.map((item) => {
    //배열 형태에 전달
    imagesUrls.push(process.env.PUBLIC_URL + item);

    //오브젝트 형태에 전달 <- 이미지갤러리 등에 쓰는 경우
    imagesObjectUrls.push({ original: process.env.PUBLIC_URL + item });
    return;
  });

  // 비디오+public 폴더 주소 합치기
  data.videos.map((item) => {
    videoUrls.push(process.env.PUBLIC_URL + item);
    return;
  });

  return (
    <>
      <Header nav={"WORKS"} />
      <ss.HeadContainer>
        <ss.HeadContainerImg imageUrl={data.link_banner} />
        {/* <ss.BackBtn onClick={() => history.goBack()}>뒤로</ss.BackBtn> */}
        <div className="titleText Holder">
          <div className="01">Creative Open Campus</div>
          <div className="02">2016</div>
        </div>
      </ss.HeadContainer>

      <IntroHolder
        title={data.title}
        client={data.client}
        period={data.period}
        subtitle={data.subtitle}
        team={data.team}
        results={data.results.map((item, idx) => {
          return (
            <span key={idx}>
              {item}
              <br />
            </span>
          );
        })}
      />

      <ss.MainContainer className={isScrolled ? "scrolled" : ""}>
        {/* SVG 이미지 로드 */}
        {/* <ss.SvgImg
          type="image/svg+xml"
          data={process.env.PUBLIC_URL + "/img/svg/aboutCircle.svg"}
          top="200%"
          left="-20%"
        /> */}

        <div className="subContainer">
          <ss.TextHolder first={true}>
            <div className="title">
              <div className="bar_start" />
              <div className="text">다양한 경험이 가능했던 융합 프로젝트</div>
              {/* <div className="bar_end" /> */}
            </div>
            <div className="desc">
              <div>
                창의+융합형 인재 육성을 위해 다학계 분야 연구원들이 모인 개방형
                캠퍼스 프로젝트입니다.
                <br />
                Unity3D를 이용하여 가상 환경을 생성하여 빔프로젝터를 이용해
                공간에 투사한 뒤, 가상 환경을 제어할 수 있는 테이블을
                제작하였습니다.
                <br />
                가상 환경은 외부 데이터를 종합적으로 이용해 공간을 사용자의 감성
                상태에 따라 변경해줍니다.
                <br />
                <br />
                전체 내용 기획을 시작으로, 공간에 영상을 투사할 다수의 프로젝터
                제어, UX를 고려한 인터페이스 구성 및 소스 제작, Unity3D를 이용한
                환경 개발, 테이블과 함께 사용한 외부 하드웨어들에서 들어오는
                데이터를 이용한 환경 제어 등을 맡아서 진행하였습니다.
              </div>
            </div>
            {/* COC 가이드 비디오 */}
            <video
              style={{ pointerEvents: "none" }}
              muted
              controls
              className="items"
              src={videoUrls[1]}
            />
          </ss.TextHolder>
        </div>
        <ss.TechContainer>
          <div className="techHolder__title">담당업무 / 기여도</div>
          <ss.TechHolder>
            {data.jobs.map((item, idx) => {
              return (
                <ContributionChart
                  key={"chart" + idx}
                  className="techHolder__chart"
                  jobName={item.job_name}
                  myJob={item.my_job}
                  withTeam={item.with_team}
                  percent={item.percentage}
                />
              );
            })}
          </ss.TechHolder>
        </ss.TechContainer>
        <div className="subContainer">
          <ss.TextHolder>
            <div className="title">
              <div className="bar_start" />
              <div className="text">Unity3D를 이용한 실시간 환경 구성</div>
              <div className="bar_end" />
            </div>
            <div className="desc">
              <div>
                실시간으로 변화하는 환경을 구성하기 위해 Unity3D를
                이용하였습니다.
                <br />
                환경은 사용자의 감성 데이터와 표정, 동작등을 인식하고 종합하여
                감성 증강을 위해 변화합니다.
                <br />
                <br />
                유니티는 외부 하드웨어들에서 데이터를 받아 통합하는데도 사용
                되었습니다.
                <br />
                외부 하드웨어로 키넥트, 웹캠, 뇌파장비, 아두이노 심박 센서를
                사용했으며 데이터들은 유니티를 거쳐 가상 공간에 반영되었습니다.
              </div>
            </div>
          </ss.TextHolder>
        </div>
        <ss.ImageHolder>
          <ss.Holder__image
            src={imagesUrls[10]}
            className="holder__image"
            alt="img02"
            column={"3 / 8 span"}
            objPos="0% 0"
          />
          <ss.Holder__video
            className="holder__video"
            autoPlay
            muted
            loop
            controls={false}
            src={videoUrls[0]}
            column={"2 / 10 span"}
          />
          <ss.Holder__image
            src={imagesUrls[2]}
            className="holder__image"
            alt="img02"
            column={"2 / 5 span"}
            objPos="0% 0"
          />
          <ss.Holder__image
            src={imagesUrls[1]}
            className="holder__image"
            alt="img02"
            column={"7 / 5 span"}
          />
          <ss.Holder__image
            src={imagesUrls[3]}
            className="holder__image"
            alt="img01"
            column={"2 / 10 span"}
            objPos="0% 0"
          />
        </ss.ImageHolder>

        <div className="subContainer">
          <ss.TextHolder>
            <div className="title">
              <div className="bar_start" />
              <div className="text">터치 테이블 형태의 사용자 인터페이스</div>
              <div className="bar_end" />
            </div>
            <div className="desc">
              <div>
                대형 테이블에 터치 기능을 구현했기 때문에, 사용자의 손이 닿는
                테이블 하단에 메인 인터페이스를 구성했습니다. 테이블 상단은 현재
                감성상태 등을 확인할 수 있는 대시보드로 사용했습니다.
                <br />
                감성과 표정, 동작인식등 다양한 인식 기능이 인지적 피로를 유발할
                수 있다고 판단하였기에 인터페이스는 최대한 간결하게
                제작했습니다.
              </div>
            </div>
          </ss.TextHolder>
        </div>

        <ss.ImageHolder>
          <ss.Holder__image
            src={imagesUrls[6]}
            className="holder__image"
            column={"2 / 5 span"}
            alt="img01"
            objPos="0% 0"
          />
          <ss.Holder__image
            src={imagesUrls[5]}
            className="holder__image"
            column={"7 / 5 span"}
            alt="img02"
            objPos="90% 0"
          />
        </ss.ImageHolder>

        <div className="subContainer">
          <ss.TextHolder>
            <div className="title">
              <div className="bar_start" />
              <div className="text">프로젝트 관련 결과물 제작</div>
              <div className="bar_end" />
            </div>
            <div className="desc">
              <div>
                프로젝트의 결과물로 책자를 제작/배포하여 결과보고에서 좋은
                점수를 받았습니다.
              </div>
            </div>
          </ss.TextHolder>
        </div>

        <ss.ImageHolder>
          <ss.Holder__image
            src={imagesUrls[7]}
            className="holder__image"
            alt="img02"
            column={"2 / span 10"}
          />
          <ss.Holder__image
            src={imagesUrls[8]}
            className="holder__image"
            alt="img02"
            column={"2 / span 5"}
          />
          <ss.Holder__image
            src={imagesUrls[9]}
            className="holder__image"
            alt="img02"
            column={"7 / span 5"}
          />
        </ss.ImageHolder>

        <div className="subContainer">
          <ss.TextHolder>
            <div className="title">
              <div className="bar_start" />
              <div className="text">프로젝트 종료 후</div>
              <div className="bar_end" />
            </div>
            <div className="desc">
              <div>
                융합 프로젝트+ 마감일이 정해져있는 프로젝트였기 때문에 강제로
                성장할 수 있었습니다. 이 때 유니티의 확장성에 아주 놀랐던 기억이
                납니다.
              </div>
            </div>
          </ss.TextHolder>
        </div>
      </ss.MainContainer>
    </>
  );
}
