import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import styled from "styled-components";

const HeaderLink = styled(Link)``;

const HeaderHolder = styled.div`
  position: fixed;
  width: 100%;
  padding: 3vh 5%;

  left: 0;
  top: 0;
  justify-content: space-between;
  display: flex;
  box-sizing: border-box;

  background-color: black;
  color: white;
  backdrop-filter: blur(5px);
  transition: 0.2s;
  z-index: 5;

  ${HeaderLink} {
    font-size: 2rem;

    & > .headerTitle {
      display: inline-block;
      padding-left: 5%;
      height: 100%;
      fill: white;

      @media screen and (max-width: 1080px) {
      }
    }

    & > .headerText {
      padding-right: 5%;
      display: inline-block;
      color: white;

      @media screen and (max-width: 1080px) {
        max-width: 80%;
        padding-left: 5%;
      }
    }
  }

  &.isScrolled {
    font-size: 1.5rem;
    padding: 1.5vh 5%;
    font-weight: 700;
    background-color: #000000b0;

    & > ${HeaderLink} {
    }
  }
`;

export default function Header(props) {
  const [active, setActive] = useState(true);
  const [isScrolled, setScrollActive] = useState(false);

  const onScroll = () => {
    if (window.scrollY > 50) {
      setScrollActive(true);
    } else {
      setScrollActive(false);
    }
  };

  useEffect(() => {
    setActive(true);
    window.addEventListener("scroll", onScroll);

    return function cleanup() {
      setActive(false);
    };
  });

  return (
    <>
      {active ? (
        <HeaderHolder className={isScrolled ? "isScrolled" : ""}>
          <HeaderLink to="" id="Home">
            <svg
              className="headerTitle"
              version="1.1"
              id="Layer_1"
              xmlns="http://www.w3.org/2000/svg"
              x="0px"
              y="0px"
              viewBox="0 20 125 80"
            >
              <rect x="5.5" y="33.17" className="st0" width="15" height="60" />
              <rect
                x="105.5"
                y="33.17"
                className="st0"
                width="15"
                height="60"
              />
              <circle className="st0" cx="42.06" cy="52.23" r="19.06" />
              <polygon
                className="st0"
                points="78.4,34.07 65.45,34.07 65.44,34.08 65.44,47.05 89.68,71.29 102.64,71.29 102.65,71.28 102.65,58.32 
	"
              />
            </svg>
          </HeaderLink>
          <HeaderLink
            to="/works"
            id="works"
            // className={isScrolled ? "isScrolled" : ""}
          >
            <div className="headerText">{props.nav}</div>
          </HeaderLink>
        </HeaderHolder>
      ) : null}
    </>
  );
}
