import { Suspense, lazy, useState, useEffect } from "react";
import styled from "styled-components";
import {
  Route,
  Routes,
  useParams,
  useLocation,
  useRouteMatch,
} from "react-router-dom";
import { LoadingPage, MainContainer } from "./sharedStyle";
import { TransitionGroup, CSSTransition } from "react-transition-group";

import Header from "../components/Header";

//Import Components
import ItemCell from "../components/ItemCell";

//Import JSON data
import JSON_DATA from "../Data3.json";

//Import pages
import { notFound, coc2016, yng, viaf, doh, gutsy, cardivu, heartfit } from ".";

const CSST = styled(CSSTransition)`
  .effect-enter {
    opacity: 0;
  }
  .effect-enter-active {
    opacity: 1;
    transition: opacity 0.7s;
  }
  .effect-exit {
    opacity: 1;
  }
  .effect-exit-active {
    opacity: 0;
    transition: opacity 0.7s;
  }
`;

export default function Works() {
  const location = useLocation();
  let { path, url } = useRouteMatch();
  console.log(path);
  console.log(url);

  return (
    <Routes location={location}>
      <Route path="" element={<WorksList />}></Route>
      <Route path=":contentsId" element={<ContentsLoader />} />
    </Routes>
    // <Routes location={location}>
    //   <Route path="" element={<WorksList />} />
    //   <Route path=":contentsId" element={<ContentsLoader />} />
    // </Routes>
  );
}

function WorksList() {
  const [isLoading, setLoading] = useState(true);
  const [isShow, setShow] = useState(true);

  const switchShow = () => {
    setShow(false);
  };
  // const ItemCell = React.lazy(() => import("../components/ItemCell"));

  //스크롤을 맨 위로
  useEffect(() => {
    window.scrollTo(0, 0);
  }, []);

  useEffect(() => {
    setLoading(false);
  }, [isLoading]);

  useEffect(() => {
    console.log(isShow);
  }, [isShow]);

  return (
    <>
      {/** 로딩 컴포넌트 만들어서 넣으면 될듯?? */}
      {/* <LoadingPage isLoading={isLoading} /> */}

      <Header nav="WORKS" />

      <MainContainer paddingToTop={"10rem"}>
        <div>
          {/* <div className={`itemList ${isShow ? "show" : "hide"}`}> */}
          {JSON_DATA &&
            JSON_DATA.map((item) => {
              return (
                <ItemCell
                  onClick={switchShow}
                  isConstruction={item.title.includes("공사중")}
                  key={item.id}
                  alt={item.id}
                  thumbImg={process.env.PUBLIC_URL + item.thumb_img}
                  title={item.title}
                  subtitle={item.subtitle}
                />
              );
            })}
        </div>
      </MainContainer>
    </>
  );
}

function ContentsLoader() {
  let { contentsId } = useParams();
  console.log("ID: " + contentsId);

  switch (contentsId) {
    case "coc2016":
      return coc2016();

    case "yng":
      return yng();

    case "viaf2021":
      return viaf();

    case "gutsy":
      return gutsy();

    case "heartfit":
      return heartfit();

    case "doh":
      return doh();

    case "cardivu":
      return cardivu();

    default:
      //return 404;
      return (
        <main style={{ padding: "1rem" }}>
          <p>There's nothing here!</p>
        </main>
      );
  }
}
