import React, { useState, useEffect } from "react";
import Header from "../components/Header";
import styled from "styled-components";

//Import Components
// import CategoryTitleDiv from "../components/CategoryTitleDiv";
import ItemCell from "../components/ItemCell";

//Import Style
import { MainContainer } from "./sharedStyle";

//Import JSON data
import JSON_DATA from "../Data3.json";

function loadingScreen() {}

export default function WorksList({ match }) {
  const path = match.path;
  const [loading, setLoading] = useState(true);
  const [loading2, setLoading2] = useState(true);

  //스크롤을 맨 위로... 상황에 따라 조건이 필요할지도
  window.scrollTo(0, 0);

  useEffect(() => {
    console.log("component mount");
    setTimeout(() => setLoading(false), 3000);

    return () => {
      setLoading(false);
      console.log("component unmount");
      clearTimeout();
    };
  }, [loading]);

  return (
    <>
      {loading ? (
        <div>
          <h1>LOADING,,,</h1>
        </div>
      ) : (
        <>
          <Header nav="Works" />

          <MainContainer paddingToTop={"10rem"}>
            <div className="itemList">
              {JSON_DATA &&
                JSON_DATA.map((item) => {
                  return (
                    <ItemCell
                      key={item.id}
                      path={path}
                      alt={item.id}
                      thumbImg={process.env.PUBLIC_URL + item.thumb_img}
                      title={item.title}
                      subtitle={item.subtitle}
                    />
                  );
                })}
            </div>
          </MainContainer>
        </>
      )}
    </>
  );
}

