import React, { useState, useEffect } from "react";
import {
  Outlet,
  Route,
  Routes,
  useLocation,
  useParams,
} from "react-router-dom";
import { createBrowserHistory } from "history";
import { TransitionGroup, CSSTransition } from "react-transition-group";
import styled from "styled-components";

import JSON_DATA from "../Data3.json";
import { Header } from "../components";
import { MainContainer } from "../pages/sharedStyle";
import { ItemCell } from "../components";
import { Welcome, Works } from "../pages/";

//Import pages
import {
  notFound,
  coc2016,
  yng,
  viaf,
  doh,
  gutsy,
  cardivu,
  heartfit,
} from "../pages";
import { GlobalStyle } from "../pages/sharedStyle";

// const history = createBrowserHistory();

const App = () => {
  const location = useLocation();
  return (
    <>
      <GlobalStyle />
      <Routes location={location} key={location.pathname + "_routes"}>
        <Route path="" element={<Welcome />} />
        {/* <Route path="/works/*" element={<Works />}> */}
        <Route path="works/*" element={<Works />} />
        <Route path="works/:contentsId" element={<ContentsLoader />} />
      </Routes>
      {/* <AnimatePresence exitBeforeEnter>
      </AnimatePresence> */}
    </>
  );
};

function WorksList() {
  const [isLoading, setLoading] = useState(true);
  const [isShow, setShow] = useState(true);

  const switchShow = () => {
    setShow(false);
  };

  //스크롤을 맨 위로
  useEffect(() => {
    window.scrollTo(0, 0);
  }, []);

  useEffect(() => {
    setLoading(false);
  }, [isLoading]);

  useEffect(() => {
    console.log(isShow);
  }, [isShow]);

  return (
    <>
      {/** 로딩 컴포넌트 만들어서 넣으면 될듯?? */}
      {/* <LoadingPage isLoading={isLoading} /> */}

      <Header nav="WORKS" />

      <MainContainer className="main_cont" paddingToTop={"10rem"}>
        <div className="itemList">
          {/* <div className={`itemList ${isShow ? "show" : "hide"}`}> */}
          {JSON_DATA &&
            JSON_DATA.map((item) => {
              return (
                <ItemCell
                  onClick={switchShow}
                  isConstruction={item.title.includes("공사중")}
                  key={item.id}
                  alt={item.id}
                  thumbImg={process.env.PUBLIC_URL + item.thumb_img}
                  title={item.title}
                  subtitle={item.subtitle}
                />
              );
            })}
        </div>
      </MainContainer>
    </>
  );
}

export default App;
