import React, { Component } from "react";
import "./Works.css";
import Header from "../components/Header";
import styled from "styled-components";

import thumb_coc from "../img/thumb_coc.jpg";
import thumb_yng from "../img/thumb_esrc_pink.jpg";
import thumb_gutsy from "../img/thumb_Gutsy.jpg";
import thumb_doh from "../img/thumb_ProjectionMapping.jpg";
import thumb_jini from "../img/thumb_MovWork.jpg";
import thumb_toyp from "../img/thumb_ProjectionMapping2.jpg";

const MainContainer = styled.div`
  height: auto;
  padding-top: 10em;
  padding-left: 17%;
  padding-right: 17%;
  background-color: white;

  @media screen and (max-width: 1080px) {
    padding-left: 10%;
    padding-right: 10%;
  }
`;

const CategoryTitleBg = styled.div`
  width: 40%;
  background-color: black;
  margin-bottom: 2vh;

  @media screen and (max-width: 1080px) {
    width: 70%;
    background-color: black;
    margin-bottom: 2vh;
  }
`;

const CategoryTitle = styled.div`
  font-size: 3em;
  font-weight: 800;
  padding-inline-start: 1em;
  padding-block-end: 0.2em;
  margin-bottom: 8px;
  color: white;

  @media screen and (max-width: 1080px) {
    font-size: 2em;
    padding-inline-start: 0.3em;
  }
`;

const ItemCell = styled.a`
  margin-bottom: 3vh;
  float: left;
  width: 50%;
  padding-left: 1.5%;
  padding-right: 1.5%;

  & > em {
    font-size: 1.1em;
  }

  & > strong {
    font-size: 1.7em;
    font-weight: 900;
  }

  @media screen and (max-width: 1080px) {
    width: 100%;
    padding-left: 3%;
    padding-right: 3%;
  }
`;


class Works extends Component {
  render() {
    return (
      <>
        <Header />
        <MainContainer>
          <div className="item-container">
            <div className="category uiux">
              <CategoryTitleBg>
                <CategoryTitle>UI/UX</CategoryTitle>
              </CategoryTitleBg>

              <ItemCell href="./contents/coc2016.html" className="item-cell">
                <div>
                  <img alt={"COC2016"} className="item-thumb" src={thumb_coc} />
                </div>
                <strong>공간 VR</strong> <br></br>
                <em>COC 2016</em>
              </ItemCell>

              <ItemCell href="./contents/yng.html" className="item-cell">
                <div>
                  <img
                    alt={"연애능력고사"}
                    className="item-thumb"
                    src={thumb_yng}
                  />
                </div>
                <strong>UI 제작</strong> <br></br>
                <em>연애능력고사</em>
              </ItemCell>

              <ItemCell href="#" className="item-cell">
                <div>
                  <img alt={"Gutsy"} className="item-thumb" src={thumb_gutsy} />
                </div>
                <strong>UI 제작 / UX 설계</strong> <br></br>
                <em>Gutsy</em>
              </ItemCell>
            </div>

            <div className="category graphics">
              <CategoryTitleBg>
                <CategoryTitle>Graphics</CategoryTitle>
              </CategoryTitleBg>
              <ItemCell href="#" className="item-cell">
                <div>
                  <img
                    alt={"Dancing on the hills"}
                    className="item-thumb"
                    src={thumb_doh}
                  />
                </div>
                <strong>프로젝션 매핑</strong> <br></br>
                <em>Dancing on the hills</em>
              </ItemCell>

              <ItemCell href="#" className="item-cell">
                <div>
                  <img
                    alt={"Empi Jini"}
                    className="item-thumb"
                    src={thumb_jini}
                  />
                </div>
                <strong>모션그래픽</strong> <br></br>
                <em>Empy jini</em>
              </ItemCell>
            </div>

            <div className="category web">
              <CategoryTitleBg>
                <CategoryTitle>Web</CategoryTitle>
              </CategoryTitleBg>
              <ItemCell href="#" className="item-cell">
                <div>
                  <img
                    alt={"Toy Projects"}
                    className="item-thumb"
                    src={thumb_toyp}
                  />
                </div>
                <strong>?</strong> <br></br>
                <em>toy project</em>
              </ItemCell>
            </div>
          </div>
        </MainContainer>
      </>
    );
  }
}

export default Works;
