import React from "react";
import { Route, Routes, useLocation } from "react-router-dom";
import { createBrowserHistory } from "history";
import { TransitionGroup, CSSTransition } from "react-transition-group";

import { Welcome, Works } from "../pages/";
import { AnimatePresence, motion } from "framer-motion";

import { GlobalStyle } from "../pages/sharedStyle";

const history = createBrowserHistory();

const App = () => {
  const location = useLocation();

  return (
    <>
      <GlobalStyle />
      <TransitionGroup></TransitionGroup>
      <Routes location={location} key={location.pathname + "_routes"}>
        <Route history={history} path="" element={<Welcome />} />
        <Route history={history} path="works/*" element={<Works />} />
      </Routes>
      {/* <AnimatePresence exitBeforeEnter>
      </AnimatePresence> */}
    </>
  );
};

export default App;
