import JSON_DATA from "../Data3.json";

export function fetchJsonData(id) {
  let dataPromise = fetchData(id);
  console.log(dataPormise);
  return { data: wrapPromise(dataPromise) };
}

function wrapPromise(promise) {
  let status = "pending";
  let result;
  let suspender = promise.then(
    (r) => {
      status = "success";
      result = r;
    },
    (e) => {
      status = "error";
      result = e;
    }
  );

  return {
    read() {
      if (status === "pending") {
        throw suspender;
      } else if (status === "error") {
        throw result;
      } else if (status === "success") {
        return result;
      }
    },
  };
}

function fetchData(id) {
  console.log("fetch data...");

  return new Promise((resolve) => {
    setTimeout(() => {
      console.log("fetched data");

      resolve(
        JSON_DATA.map((item) => {
          if (item.id == id) {
            console.log("api id:" + id);
            const rObj = {};
            rObj[item.id] = item;
            console.log(rObj);
            return rObj;
          }
        })
      );
    });
  });
}
