import styled from "styled-components";
import { createGlobalStyle } from "styled-components";
// Shared style

export const colors = {
  primary: "#101010",
  secondary: "#ffffff",
  background: "#f0f0f0",
};

export const GlobalStyle = createGlobalStyle`
  
  html {
    margin: 0;
    overflow-x: hidden;
    font-family: 'Noto Sans KR', sans-serif;
    font-size: 18px;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
  }

  a {
    text-decoration: none;
    color: black;
  }

  a:visited {
    color: black;
    text-underline-position: unset;
  }

  a:hover {
    color: rgb(80, 80, 80);
  }
`;

export const HeadContainer = styled.div`
  height: 100vh;
  padding-top: 10vh;
  overflow: hidden;

  @media screen and (max-width: 1080px) {
    height: 90vh;
  }
`;

export const HeadContainerImg = styled.div`
  height: 100%;
  background: no-repeat center/cover;
  background-image: url(${(props) => process.env.PUBLIC_URL + props.imageUrl});
`;

export const HeadContainerVid = styled.video`
  width: auto;
  margin-left: auto;
  margin-right: auto;
  display: block;
`;

export const HeaderTextHolder = styled.div`
  position: absolute;
  width: auto;
  top: 25%;
  background-color: #262626;
  text-align: left;
  display: block;
  padding: 5vh 10vh 5vh 8vh;
  color: white;

  & > .title {
    font-size: 5rem;
    font-weight: 700;
    margin-bottom: 10vh;
    line-height: 6.5rem;

    @media screen and (max-width: 1080px) {
      font-size: 3rem;
      line-height: 3.5rem;
      top: 30%;
    }
  }

  & > .subtitle {
    font-size: 3rem;
    font-weight: 500;
  }

  & > .desc {
    font-size: 2rem;
    font-weight: 500;

    @media screen and (max-width: 1080px) {
      bottom: 0;
      margin-left: 0%;
    }
  }
`;

export const MainContainer = styled.div`
  height: auto;
  overflow: auto;
  padding-top: ${(props) => (props.paddingTop ? props.paddingTop : "10em")};
  padding-bottom: 10em;
  background-color: #f9f9f9;

  /* &.scrolled {
    background-color: white;
  } */

  & > .subContainer {
    padding-left: 15%;
    padding-right: 15%;
  }

  @media screen and (max-width: 1080px) {
    padding-left: 4%;
    padding-right: 4%;
  }
`;

export const TextBlock = styled.div``;

export const ContentsContainer = styled.div`
  height: auto;
  padding-top: 10em;
  padding-left: 17%;
  padding-right: 17%;
  background-color: gray;

  @media screen and (max-width: 1080px) {
    padding-left: 10%;
    padding-right: 10%;
  }
`;
