import React, { useState, useEffect } from "react";
import styled, { css } from "styled-components";
import { MainContainer } from "./sharedStyle";
import JSON_DATA from "../Data2.json";
import Header from "../components/Header";
import Items from "./Contents__Items";

const HeadContainer = styled.div`
  height: 100vh;
  padding-top: 10vh;
  overflow: hidden;

  @media screen and (max-width: 1080px) {
    height: 90vh;
  }
`;

const HeadContainerImg = styled.div`
  height: 100%;
  background: no-repeat center/cover;
  background-image: url(${(props) => process.env.PUBLIC_URL + props.imageUrl});
`;

const HeadContainerVid = styled.video`
  width: auto;
  height: 100%;
  margin-left: auto;
  margin-right: auto;
  display: block;
`;

const HeaderTextHolder = styled.div`
  position: absolute;
  width: auto;
  top: 25%;
  background-color: #262626;
  text-align: left;
  display: block;
  padding: 5vh 10vh 5vh 8vh;
  color: white;

  & > .title {
    font-size: 5rem;
    font-weight: 700;
    margin-bottom: 10vh;
    line-height: 6.5rem;

    @media screen and (max-width: 1080px) {
      font-size: 3rem;
      line-height: 3.5rem;
      top: 30%;
    }
  }

  & > .subtitle {
    font-size: 3rem;
    font-weight: 500;
  }

  & > .desc {
    font-size: 2rem;
    font-weight: 500;

    @media screen and (max-width: 1080px) {
      bottom: 0;
      margin-left: 0%;
    }
  }
`;

const ColumnTitle = styled.div`
  position: relative;
  left: -1%;
  font-size: 2em;
  display: inline-block;
  width: auto;
  padding: 0 1em 0 0.4em;
  height: 2em;
  line-height: 1.8em;
  margin-bottom: 1em;
  margin-top: ${(props) => (props.first ? "0em" : "4em")};
  background-color: black;
  color: white;
`;

const TextHolder = styled.div`
  display: flex;
  flex-direction: column;
  margin-top: ${(props) => (props.first ? "0rem" : "4rem")};
  margin-bottom: 30px;
  margin-right: 50px;
`;

const TextHolderHorizontal = styled.div`
  display: flex;
  margin-right: 100px;
`;

const HolderTitle = styled.span`
  font-weight: 600;
`;

const HolderDesc = styled.span`
  white-space: pre-line;
  margin-left: 0px;
  margin-top: 5px;
`;

const HolderDescHorizontal = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 1em;
  margin-left: 0px;
  margin-top: 5px;
`;

const TechContainer = styled.div`
  width: 30%;
  height: 10em;
  justify-content: center;
  background-color: #f5f5f5;
  border-radius: 15px;

  .tech-tmb {
    position: relative;
    top: 10%;
    left: 50%;
    transform: translate(-50%);
    width: 5em;
    height: 5em;
    background-color: #757575;
    border-radius: 5%;
  }

  .tech-title {
    position: relative;
    text-align: center;
    top: 13%;
  }
`;

const BackBtn = styled.div`
  position: relative;
  top: 5%;
`;

export default function Contents({ match, history }) {
  // JSON 파일을 만들어서 ContentsId랑 일치하는 오브젝트 불러오기
  const contentsId = match.params.contentsId;
  const [isScrolled, setScrollActive] = useState(false);
  const [backType, setBackType] = useState("img");
  const textData = {};

  // const onScroll = () => {
  //   if (window.scrollY > 500) {
  //     setScrollActive(true);
  //   } else {
  //     setScrollActive(false);
  //   }
  // };

  window.scrollTo(0, 0);

  JSON_DATA.map((item, idx) => {
    // JSON 오브젝트 나누기
    const [keys, entryValues] = Object.entries(item);
    keys[1].map((element) => {
      if (element.id == contentsId) {
        textData[0] = element;
        return;
      }
    });
  });
  const data = textData[0];

  useEffect(() => {
    setBackType(data.banner_type);
  });

  //Render
  return (
    <>
      <Header nav={"works"} />
      <HeadContainer>
        {backType === "img" && (
          <HeadContainerImg
            imageUrl={data.link_banner}
            bannerType={data.banner_type}
          ></HeadContainerImg>
        )}
        {backType === "vid" && (
          <>
            <HeadContainerVid autoPlay muted loop bannerType={data.banner_type}>
              <source
                src={process.env.PUBLIC_URL + data.link_banner}
                type="video/mp4"
              ></source>
            </HeadContainerVid>
          </>
        )}
        <BackBtn onClick={() => history.goBack()}>뒤로</BackBtn>
        <HeaderTextHolder>
          <div className="title">{data.title}</div>
          <div className="subtitle">{data.description}</div>
          <div className="desc">{data.period}</div>
        </HeaderTextHolder>
        {/* <HeaderTitle>{data.title}</HeaderTitle>
        <HeaderDesc>
          {data.period} {data.team}
        </HeaderDesc> */}
      </HeadContainer>

      <MainContainer className={isScrolled ? "scrolled" : ""} paddingTop="5em">
        <TextHolder>
          <HolderDesc>{data.abstract}</HolderDesc>
        </TextHolder>

        <TextHolder>
          <Items name={data.id}></Items>
        </TextHolder>
        <TextHolder>
          <HolderTitle>프로젝트에 사용한 기술</HolderTitle>
          <HolderDesc>
            <ul>
              {data.method.map((item, key) => {
                return <li key={key}>{item}</li>;
              })}
            </ul>
          </HolderDesc>
        </TextHolder>

        <TextHolder>
          <HolderTitle>담당업무 / 기여도</HolderTitle>
          <HolderDescHorizontal>
            {data.my_job.map((item, idx) => {
              const [key, val] = Object.entries(item);
              // console.log(key);
              // console.log(item.key);
              return (
                <TechContainer key={idx}>
                  <div className="tech-tmb"></div>
                  <div className="tech-title">{key[0]}</div>
                  {/* <div className="tech-percent">{key[1]}</div> */}
                </TechContainer>
              );
            })}
          </HolderDescHorizontal>
        </TextHolder>

        {/* Result */}
        <ColumnTitle>결과</ColumnTitle>
        <TextHolder>
          <HolderTitle>자체 평가</HolderTitle>
          <HolderDesc>{data.earned}</HolderDesc>
        </TextHolder>
      </MainContainer>
    </>
  );
}
